mat2 rotate(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float glow(vec2 p, float radius, float intensity) {
    float dist = length(p);
    return pow(radius / dist, intensity);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;

    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float t = iTime * 0.3;

    uv *= rotate(t * 0.8);

    float len = length(uv);
    vec2 angle = atan(uv) * 3.0;

    float spiral = sin(angle.x * 8.0 + len * 12.0 - t * 8.0) * 0.5 + 0.5;
    spiral *= sin(angle.y * 6.0 + len * 14.0 - t * 5.0) * 0.5 + 0.5;

    vec3 color = vec3(0.4, 0.2, 1.0) + vec3(0.7, 0.9, 0.3) * sin(t + len * 4.0);
    color *= spiral;

    float neonGlow = glow(uv, 0.3, 2.0) * 1.5;

    float glowEffect = smoothstep(0.7, 0.2, len);

     fragColor = vec4(color * glowEffect + vec3(0.5, 0.5, 0.0) * neonGlow, 1.0);

    float lightIntensity = glow(uv, 0.3, 3.0);
    fragColor.rgb += vec3(1.0, 0.8, 0.6) * lightIntensity * spiral;
}
