mat2 rotate2D(float angle)
{
    return mat2(cos(angle),-sin(angle),sin(angle),cos(angle));
}

vec2 swirl(vec2 uv, vec2 center, float radius, float swirl_strength) {
    vec2 toCenter = uv - center;
    float dist = length(toCenter);

    if (dist > radius) {
        return uv;
    }

    float angle = atan(toCenter.y, toCenter.x);
    float newAngle = angle + swirl_strength * (radius - dist) / radius;

    float x = cos(newAngle) * dist;
    float y = sin(newAngle) * dist;

    return center + vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy)/iResolution.y;
    uv = rotate2D(-iTime)*uv;
    vec3 col1 = vec3(0.,0.5,0.99);
    vec3 col2 = vec3(0.5,0.7,0.8);

    vec2 swirl1 = swirl(uv,vec2(0.),1.1,16.);
    vec3 col = col1*vec3(max(swirl1.x,swirl1.y))+col2*(1.-vec3(min(swirl1.x,swirl1.y)));
    col = mix(col, 1.-col, sin(5.*length(uv)-.5*iTime)*.5 + .5);


    fragColor = vec4(col,1.0);
}
