void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 0.2;
    uv = (gl_FragCoord.xy / iResolution.xx - 0.51) * 8.0;

    float a_1 = 1.0;
    float b_1 = 1.0;
    float c_1 = 1.0;

    for (int s = 0; s < 10; s++) {
        float x = cos(uv.y * a_1 + time / b_1);
        float y = sin(uv.x * a_1 + time / b_1);

        vec2 r = vec2(x, y) / c_1;
        uv.xy += r;

        a_1 *= 1.93;
        b_1 *= 1.15;
        c_1 *= 1.7;
    }

    float b = sin(((uv.x + uv.y) * 0.1 + sin(time * 0.1)));

    fragColor = vec4(0, b - 0.7, b, 1.0);
}

const float PHI = 1.61803398874989484820459;

float gold_noise(in vec2 xy)
{
    return ceil(tan(distance(xy*PHI, xy)));
}

vec2 mainSound(int samp, float time) 
{
    float dist = fract(distance(uv, vec2(1.)));

    return vec2(gold_noise(vec2(iSampleRate*time*dist)));
}
