const vec4 notWhite = vec4(255, 249, 242, 1) / 255.0;
const vec4 longan = vec4(255, 216, 168, 1.0) / 255.0;
const vec4 mango = vec4(255, 171, 92, 1.0) / 255.0;
const vec4 strawberry = vec4(229, 107, 111, 1.0) / 255.0;
const vec4 cherry = vec4(131, 37, 79, 1.0) / 255.0;
const vec4 notBlack = vec4(25, 25, 25, 1) / 255.0;

vec4 col(float x) {
  if (x > 0.6) return notWhite;
  if (x > 0.5) return longan;
  if (x > 0.3) return mango;
  if (x > 0.0) return strawberry;
  if (x > -0.5) return cherry;
  return notBlack;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy/2.0)*2.0/iResolution.y;

    float sun = 0.5 - iTime / 60.0;

    float addBit = 0.0;

    if (uv.y < 0.0) {
        float wave = iTime - 55.0 / (uv.y * 3.0 - .7);
        addBit += 0.3 + cos(wave) * 0.1;
        uv.y = -uv.y;
    }
    if (uv.y < 0.2) {
        float diff = (0.2 - uv.y) / 0.2;
        addBit += diff * diff * diff;
    }

    float val = 1.0 - length(uv - vec2(0, sun)) * 0.8;
    fragColor = col(val + addBit);
}
