// Australian Flag by Rik Riesmeijer (CC0)
float star5(vec2 v) {
    mat2 z = mat2(v.x, -v.y, v.y, v.x);
    z = z * z * z * z * z - mat2(1);
    return smoothstep(2.5, 1.5, determinant(z));
}

float star7(vec2 v) {
    mat2 z = mat2(v.x, -v.y, v.y, v.x);
    z = z * z * z * z * z * z * z - mat2(1);
    return smoothstep(2.5, 1.5, determinant(z));
}

void flagAU(out vec4 c, vec2 v) {
    v = (v - 0.5 * iResolution.xy) * 30.0;
    v = v.yx / min(iResolution.x, iResolution.y) + vec2(0, 0.5);
    c = vec4(0, 0, 0.5451, 1);
    c = (v.x + v.y * 0.5 > -1.0 && v.x + v.y * 0.5 < 2.0 ||
         v.x - v.y * 0.5 > 12.5 && v.x - v.y * 0.5 < 15.5)?
        (-v.x < 0.0 && v.y < 0.0) ?  vec4(1) : c : c;
    c = (v.x + v.y * 0.5 > 0.0 && v.x + v.y * 0.5 < 1.0 ||
         v.x - v.y * 0.5 > 13.5 && v.x - v.y * 0.5 < 14.5)?
         (-v.x < 0.0 && v.y < 0.0) ? vec4(1, 0, 0, 1) : c : c;
    c = ((v.x > 5.3 && v.x < 9.7 || -v.y > 11.0 && -v.y < 16.0))?
         (-v.x < 0.0 && v.y < 0.0) ? vec4(1) : c : c;
    c = ((v.x > 6.5 && v.x < 8.5 || -v.y > 12.0 && -v.y < 15.0))?
         (-v.x < 0.0 && v.y < 0.0) ? vec4(1, 0, 0, 1) : c : c;
    c = mix(vec4(0, 0, 0.5451, 1), c,
            smoothstep(0.15, 0.2, smoothstep(0.5,  2.0, v.x) *
                                  smoothstep(0.5, -2.0, v.y)));
    v.y = v.y * 0.9 - 1.8;
    c = mix(c, vec4(1),
            star5(v       + vec2( 2.0, -12.0)) +
            star7(v / 3.0 + vec2( 2.0,   4.5)) +
            star7(v / 1.5 + vec2( 7.0, - 6.0)) +
            star7(v / 1.5 + vec2(-2.0, - 2.0)) +
            star7(v / 1.5 + vec2(-7.0, - 6.0)) +
            star7(v / 1.5 + vec2(-3.5, -10.0)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    flagAU(fragColor, fragCoord);
}
