// Brazilian Flag By Rik Riesmeijer (CC0).
void flagBR(out vec4 c, vec2 v) {
    v  = v + v - iResolution.xy;
    v /= min(iResolution.x, iResolution.y);
    c = (abs(v.x * 0.75) > abs(v.y - 0.75) || 
         abs(v.x * 0.75) > abs(v.y + 0.75) ||
         abs(v.y) > 0.75) ?
                vec4(  0, 151,  57, 255) / 255.0
              : vec4(254, 221,   0, 255) / 255.0;
    c = mix(mix(vec4(255, 255, 222, 255) / 255.0,
                vec4(  1,  33, 105, 255) / 255.0,
                smoothstep(0.05, 0.06, abs(v.y + v.x * 0.2 - 0.03))),
                c, smoothstep(0.2, 0.21, dot(v, v)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    flagBR(fragColor, fragCoord);
}
