void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float wave = texture(iChannel0, uv).x;
    float lastWave = texture(iChannel1, uv).x;


    float diff = abs(wave - lastWave);

    vec3 col = vec3(
        clamp((wave + lastWave) * 0.5 - uv.y, 0.0, 1.0) * iResolution.y * 0.01 / diff
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, fragCoord/iResolution.xy);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = texture(iChannel0, vec2(fragCoord.x/iResolution.x, 1.0));
}
