float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

vec2 getCoord() {
    return vec2(
    	texelFetch(iChannel0, ivec2(0, 0), 0).r,
        texelFetch(iChannel0, ivec2(1, 0), 0).r
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

	float d = sdCircle(p-(getCoord()/iResolution.xy),0.5);

	// coloring             outside            inside
    vec3 col = (d>0.0) ? vec3(0.9,0.6,0.3) : vec3(1.0,0.4,0.7);
    col *= 1.0 - exp(-60.0*abs(d));
	//col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.01,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdCircle(m,0.5);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}

const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;

const float speed = 1000.0;

float sdCircle( in vec2 p, in float r ) 
{
    return length(p)-r;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float outData = 0.0;

    switch(int(fragCoord.x)) {
        case 0:
        	outData = texelFetch(iChannel0, ivec2(0), 0).r +
                (iTimeDelta * speed) * texelFetch(iChannel1, ivec2(KEY_RIGHT, 0), 0).r -
                (iTimeDelta * speed) * texelFetch(iChannel1, ivec2(KEY_LEFT, 0), 0).r;
        	break;

        case 1:
        	outData = texelFetch(iChannel0, ivec2(1, 0), 0).r +
                (iTimeDelta * speed) * texelFetch(iChannel1, ivec2(KEY_UP, 0), 0).r -
        		(iTimeDelta * speed) * texelFetch(iChannel1, ivec2(KEY_DOWN, 0), 0).r;
        	break;
    }

    fragColor = vec4(outData, 0.0, 0.0, 1.0);
}
