#define MAX_STEPS 100
#define MAX_DIST 250.
#define SURF_DIST .001
#define TAU 6.283185
#define PI 3.141592

const float globalAmbient = 0.4; // how strong is the ambient lightning
const float globalDiffuse = 1.0; // how strong is the diffuse lightning
const float globalSpecular = 0.0; // how strong is the specular lightning
const float globalSpecularExponent = 16.0; // how focused is the shiny spot
const vec3 lightPos = vec3(1.0, 3.0, 5.0); // position of the light source
const vec3 lightColor = vec3(1.0); // color of the light source
const vec3 ambientColor = vec3(0.584,0.529,0.427); // ambient color

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}


float smin( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float ellipsoid( in vec3 p, in vec3 r )
{
    float k1 = length(p/r);
    float k2 = length(p/(r*r));
    return k1*(k1-1.0)/k2;
}

float capsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}

float tetrahedron( vec3 p, float h )
{
  float m2 = h*h + 0.25;

  p.xz = abs(p.xz);
  p.xz = (p.z>p.x) ? p.zx : p.xz;
  p.xz -= 0.5;

  vec3 q = vec3( p.z, h*p.y - 0.5*p.x, h*p.x + 0.5*p.y);

  float s = max(-q.x,0.0);
  float t = clamp( (q.y-0.5*p.z)/(m2+0.25), 0.0, 1.0 );

  float a = m2*(q.x+s)*(q.x+s) + q.y*q.y;
  float b = m2*(q.x+0.5*t)*(q.x+0.5*t) + (q.y-m2*t)*(q.y-m2*t);

  float d2 = min(q.y,-q.x*m2-q.y*0.5) > 0.0 ? 0.0 : min(a,b);

  return sqrt( (d2+q.z*q.z)/m2 ) * sign(max(q.z,-p.y));
}

vec2 GetDist(vec3 p) {  
    float d = 999.0;
    float mat = 0.0;
    float scale = 0.7;

    p /= scale;

    float s = ellipsoid(p,vec3(1.8,1.2,1.8)*1.3);

    s = max(s,-p.y-0.8);

    float ear = 999.0;

    {
        vec3 ep = p;

        ep /= 0.9;

        ep.x = abs(ep.x);

        ep -= vec3(1.4,1.0,1.2);

        ep.yz *= Rot(-0.2);
        ep.xy *= Rot(0.5);

        ear = tetrahedron(ep,0.8);

        ear *= 0.9;
    }

    s = smin(s,ear,0.1);

    float eyes = 999.0;

    {
        vec3 ep = p;

        ep.x = abs(p.x);

        ep -= vec3(0.6,0.2,2.3);

        ep.xy *= Rot(0.08);

        eyes = capsule(ep.zxy,0.3,0.025);
    }

    s = min(s,eyes);

    float mouth = 999.0;

    {
        vec3 mp = p;

        mp -= vec3(-0.18,-0.3,2.4);

        float k = abs(mod(mp.x*20.0,6.0) - 3.0);

        mp.y += k*0.025;

        mouth = capsule(mp.zxy,0.3,0.025);
    }

    s = min(s,mouth);

    float whiskers = 999.0;

    {
        vec3 wp = p;

        wp.xy = abs(wp.xy);

        wp -= vec3(1.3,0.026,2.1);

        wp.xy *= Rot(-0.15);

        float k = sin((wp.x - 5.0) * 5.0)* 0.5 + 0.5;

        k *= 0.02;

        wp.y += k;

        whiskers = capsule(wp.zxy,0.7,0.025);
    }

    s = min(s,whiskers);

    d = min(d,s);
    mat = (d==s) ? 0.0 : mat;

    mat = (s==eyes) ? 1.0 : mat;
    mat = (s==mouth) ? 1.0 : mat;
    mat = (s==whiskers) ? 1.0 : mat;

    d *= scale;

    return vec2(d,mat);
}

vec3 RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;
    float mat;

    float k = 0.0;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        vec2 dS = GetDist(p);
        dO += dS.x;
        mat = dS.y;

        k += 1.0;
        if(dO>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return vec3(dO,mat,k);
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(.001, 0);
    vec3 n = GetDist(p).x - 
        vec3(GetDist(p-e.xyy).x, GetDist(p-e.yxy).x,GetDist(p-e.yyx).x);

    return normalize(n);
}

float ambientOcclusion(vec3 p, vec3 n){
    const int steps = 1;
    const float delta = 0.5;

    float a = 0.0;
    float weight = 0.75;
    float m;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta;
        a += weight*(d - GetDist(p + n*d).x);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

float softshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=0; i<100; i++ )
    {
		float h = GetDist( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += h*.25;
        if( h<0.001 || t>tmax ) break;
    }
    return clamp( res, 0.1, 1. );
}


vec3 shade(  vec3 position,
                           vec3 lightPosition,
                           vec3 ambientCol,
                           vec3 lightCol,
                           float ambientCoeff,
                           float diffuseCoeff,
                           float specularCoeff,
                           float specularExponent,
                           vec3 camera_pos,
                           vec3 ray_direction
)
{
      vec3 normal = GetNormal(position);
      vec3 toEye = normalize(camera_pos - position);
      vec3 toLight = normalize(lightPosition - position);
      vec3 reflection = reflect(-toLight, normal);

      vec3 ambientFactor = ambientCol * ambientCoeff;
      vec3 diffuseFactor = diffuseCoeff * lightCol * round(max(0.0, dot(normal, toLight)) + 0.3);
      vec3 specularFactor = lightCol * round(pow(max(0.0, dot(toEye, reflection)), specularExponent))
                     * specularCoeff;

      float ao = ambientOcclusion(position+normal*0.1,normal);
      float ss= softshadow(position,lightPos,0.1,10.0);

    diffuseFactor *= round(ss);
    diffuseFactor *= ao;

    //diffuseFactor = round(diffuseFactor);   

    return ambientFactor + diffuseFactor + specularFactor;
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 
        f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u;
    return normalize(i);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-0.64*PI+1.);
    ro.xz *= Rot(-0.47*TAU);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0), 0.5);
    vec3 bg = vec3(0.6);
    vec3 col = bg;

    vec3 rm = RayMarch(ro, rd);
    float d = rm.x;

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        if (rm.y == 0.0) {
            col = vec3(0.769,0.773,0.718);
        } else if (rm.y == 1.0) {
            col = vec3(-10.3);
        }

        col *= shade(p,lightPos,ambientColor,lightColor,globalAmbient,globalDiffuse,globalSpecular,globalSpecularExponent,ro,rd);
        col = mix(col,bg,1.0 - exp(-0.0004*d*d));
    }

    float c = 0.7;
    float b = 0.0;
    col = c * (col-0.5) + 0.5 + b;
    vec3 greyscale = vec3(dot(col,vec3(0.299,0.587,0.114)));
    col = mix(greyscale,col,1.3);
    col = clamp(col,vec3(0.0),vec3(1.0));

    col = pow(col, vec3(.4545));	// gamma correction


    if (rm.z > 30.0) {
        col *= 0.0;
    }

    fragColor = vec4(col,1.0);
}
