void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalize pixel coordinates
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Define eye centers
    vec2 leftEyeCenter = vec2(-0.39, 0.0);
    vec2 rightEyeCenter = vec2(0.39, 0.0);

    // Calculate distance from pixel to eye centers
    float leftEyeDist = length(uv - leftEyeCenter);
    float rightEyeDist = length(uv - rightEyeCenter);

    // Create SDFs for eyes
    float leftEyeSDF = leftEyeDist - 0.14;
    float rightEyeSDF = rightEyeDist - 0.14;

    // Combine SDFs
    float eyesSDF = min(leftEyeSDF, rightEyeSDF);

    // Create radiating effect
    float radialEffect = sin(eyesSDF * 30.0 - iTime * .95) * 10.9 + 0.5;

    // Combine SDF with radiating effect
    float finalEffect = smoothstep(0.0, 0.0, abs(eyesSDF)) * radialEffect;

    // Create a static circle surrounding both eyes
    float surroundingCircleSDF = length(uv) - 1.;
    float surroundingCircle = smoothstep(0.0, .1, surroundingCircleSDF);

    // Combine the eyes effect with the surrounding circle
    finalEffect = max(finalEffect, surroundingCircle);

    // Output black and white color
    fragColor = vec4(vec3(finalEffect), 1.0);
}
