#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h + 4.*vec3(1,2,0)   )*.6 + .5 )

#define H2(h)  (  cos(  h + 5.*vec3(1,2,3)   )*.7 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .7*R.y, 0.))*300.;

    float sc,dotp,totdist=0., tt=iTime/3., t=0.;

    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist);

        p.xyz += vec3(6000,0,-10000); 

        p.xz *= rot( tt + abs(sin(tt/2.)));



        p.yzw = p.xyz;



        sc = 1.; 

        p.zw *= rot(1.);

        vec4 w = p;

        for (float j=0.; j<8.; j++) {

            p = abs(p)*.5;

            dotp = max(1./dot(w.xy,w.xy),.01);
            sc *= dotp; 

            p = p * dotp - .35; 

            w = log(1.+log(1.+log(1.+p)*p)*p) - .1;


        }

        float dist = abs( length(p.zw)-.06)/sc ;  
        float stepsize = dist/20.;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count
        c +=
            .015 * H2(  atan( p.w, p.z )) * exp(-totdist/5e1) 
           + .02 * H(M(sc)) *  exp(-i*i*stepsize*stepsize/5000.*(1.+1000.*abs(sin(tt*7.))) );
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
