vec2 hash( vec2 p )
{
	p = vec2( dot(p,vec2(127.1,311.7)), dot(p,vec2(269.5,183.3)) );
	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; 
    const float K2 = 0.211324865; 

	vec2  i = floor( p + (p.x+p.y)*K1 );
    vec2  a = p - i + (i.x+i.y)*K2;
    float m = step(a.y,a.x); 
    vec2  o = vec2(m,1.0-m);
    vec2  b = a - o + K2;
	vec2  c = a - 1.0 + 2.0*K2;
    vec3  h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );
	vec3  n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));
    return dot( n, vec3(70.0) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    vec2 sv = uv;

    float sTime = 3.1415*1.5;
    sv *= mat2(cos(sTime),-sin(sTime),sin(sTime),cos(sTime));

    float r = length(sv)*.5;
    float d = atan(sv.y,sv.x)*3.1415;
    vec2 polar = vec2(d,r);

    polar.x += 0.5*noise(sv+vec2(0.05*iTime));

    float f = 0.0;
    uv = polar;
	uv *= 5.0;

    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
	f = 0.5000*noise( uv ); uv = m*uv;
	f += 0.2500*noise( uv ); uv = m*uv;
	f += 0.1250*noise( uv ); uv = m*uv;
	f += 0.0625*noise( uv ); uv = m*uv;

	f = 0.1 +0.1*f;

    float ringShadow = smoothstep(0.45,0.19,length(polar.y))
                -smoothstep(0.185,0.184,length(polar.y));

    float ringPath = smoothstep(0.19,0.185,length(polar.y))
                -smoothstep(0.185,0.182,length(polar.y));

    float ringProgress = smoothstep(0.19,0.185,length(polar.y))
                -smoothstep(0.185,0.182,length(polar.y));

    float start = abs(sin(iTime*0.2));
    float width = 0.01;

    //Draw actual progress (from 0 to 1):
    ringProgress *= smoothstep(start + width, start,-atan(sv.y,sv.x)/6.2415 + 0.5);            

    //Add start and end line smooth:
    ringProgress *= smoothstep(0.0, 0.01, -atan(sv.y,sv.x)/6.2415 + 0.5);            
    ringProgress *= smoothstep(1.,0.99, -atan(sv.y,sv.x)/6.2415 + 0.5);            

    float rays = clamp(f*ringShadow, 0., 1.);
    float circle = ringProgress;
    vec3 color = col;

    col = mix(circle+rays,rays,rays)*color+0.1*ringPath*color;


    fragColor = vec4(col,1.0);
}
