#define size 25.

float Xor(float a, float b){
    return a*(1.-b) + b*(1.-a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.05,0.0,0.275);

    uv = uv / mix(1., dot(uv, uv), -.8);

    vec2 uvPow = vec2(
        pow(abs(uv.x), 2.0),
        pow(abs(uv.y), 2.0)
    );
    float vignette = 1.-length(uvPow);

    vec2 gv = fract(uv*size) - .5;
    vec2 id = floor(uv*size) + .5;

    float t = iTime * .8;

    for(int i=0; i<3; i++){
        float m = 0.;
        t += float(i)*.15;
        for(float y=-1.; y<=1.; y++) {
            for(float x=-1.; x<=1.; x++) {
                vec2 offs = vec2(x, y);
                float d = length(gv-offs);

                float tRad = sin(length(sin(t*.05)*(id+offs))*4.5-t)*.5+.5;
                float r = mix(.1, .9, tRad);

                m = Xor(m, smoothstep(r, r*.95, d));
            }
        }
        m *= .8;
        m += .2;
        if(i == 0){
            col.r += m;
        }
        if(i == 1){
            col.g += m;
        }
        if(i == 2){
            col.b += m;
        }
    }

    col *= vignette;

    fragColor = vec4(col,1.0);
}
