// Using code from

//Morgan McGuire for the noise and random functions
// https://www.shadertoy.com/view/4dS3Wd

// TDM for the getSkyColor function
// https://www.shadertoy.com/view/Ms2SD1

#define time iTime
#define depth 40.0
#define fogSize 25.0
#define seuil 4.0
#define steps 200.0
#define sand_color_1 vec3(0.55,0.6,0.4)
#define sand_color_2 vec3(0.65,0.7,0.4)
#define water_color_1 vec3(0.3,0.4,0.4)
#define water_color_2 vec3(0.3,0.465,0.441)
#define fog_color vec3(0.85,0.85,0.85)

float fogCoef=1.0/(depth-fogSize);


float PI=acos(-1.0);

float random (in float x) {
	return fract(sin(x)*1e4);
}


float noise(in vec3 p_water) {
	const vec3 step = vec3(110.0, 241.0, 171.0);

	vec3 i = floor(p_water);
	vec3 f = fract(p_water);

	// For performance, compute the base input to a
	// 1D random from the integer part of the
	// argument and the incremental change to the
	// 1D based on the 3D -> 1D wrapping
	float sand_normal = dot(i, step);

	vec3 u = f * f * (3.0 - 2.0 * f);
	return mix( mix(mix(random(sand_normal + dot(step, vec3(0,0,0))),
	random(sand_normal + dot(step, vec3(1,0,0))),
	u.x),
	mix(random(sand_normal + dot(step, vec3(0,1,0))),
	random(sand_normal + dot(step, vec3(1,1,0))),
	u.x),
	u.y),
	mix(mix(random(sand_normal + dot(step, vec3(0,0,1))),
	random(sand_normal + dot(step, vec3(1,0,1))),
	u.x),
	mix(random(sand_normal + dot(step, vec3(0,1,1))),
	random(sand_normal + dot(step, vec3(1,1,1))),
	u.x),
	u.y),
	u.z);
}

mat2 rot(float a) {
	float ca=cos(a);
	float sa=sin(a);
	return mat2(ca,sa,-sa,ca);
}

float water(in vec3 p_water, float scale ) {
	vec3 tunnel = vec3(p_water.x+sin(length(p_water*0.2)+time)*2.0,p_water.y+cos(length(p_water*0.2)-time)*2.0,0.0);
	float coef = length(tunnel)-4.0;

	float c=1.0;
	float n1=1.0;
	float d=1.0;
    vec3 noiseInput;

    p_water.xy*= rot(time);
    p_water.z=p_water.z+time;
	for(int i=0; i<8; ++i) {

        noiseInput = vec3(p_water*c-0.5*c*d);

		n1+=2.0/c*abs(noise(noiseInput*scale));
		c*=2.0;
		d+=1.5;
	}

	return n1*coef;

}
float sand(in vec3 p_water,  float scale) {
	vec3 tunnel = vec3(p_water.x,p_water.y,0.0);
	p_water.z=p_water.z+time;
	float c1=1.0;
	float n1=1.0;
	float d=1.0;
	for(int i=0; i<8; ++i) {
		n1+=4.0/c1*abs(noise((p_water*c1)*scale*0.5));
		c1*=2.0;
		d+=1.5;
	}
    float coef = length(tunnel)-1.3*n1;
	return n1*coef;

}
float mapHyper(vec3 p_water){
	return water(p_water,0.3);
}  
float mapHyper2(vec3 p_water){
	return sand(p_water,0.3);
}  


vec3 tunnel(vec3 p_water){
	vec3 off=vec3(0);
	off.x += sin(p_water.z*0.2)*1.5;
	off.y += sin(p_water.z*0.3)*1.3;
	return off;
}


vec3 getSkyColor(vec3 e) {
	e.y = (e.y);
	e.y = max(e.y,0.0);
	return vec3(pow(1.0-e.y,2.0), 1.0-e.y, 0.6+(1.0-e.y)*0.4)*noise(e);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{


    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);
    vec3 s=vec3(-1.0,-1.0,-3);
    float t2=(time*1.5);
    s.xz *= rot(sin(t2)*0.015);
    vec3 t=vec3(0,0,0);
    s -= tunnel(s);
    t -= tunnel(t);
    s.x += cos(t2*0.2)*1.0*sin(time*0.01);
    s.y += sin(t2*0.2)*1.0*sin(time*0.01+10.0);
    vec3 cz=normalize(t-s);
    vec3 cx=normalize(cross(cz,vec3(0,1,0)));
    vec3 cy=normalize(cross(cz,cx));
    vec3 r=normalize(uv.x*cx+uv.y*cy+cz*0.7);
    vec3 p_water=s;
    float c= 0.0;
    float mH;

    for(int i=0; i<200; ++i) 
    {

        mH = mapHyper(p_water);
        c +=mH;  
        if(c>seuil)break;
        p_water+=r*(mH-seuil)*0.09;
    }
    vec3 p_sand=s;
    c= 0.0;
    for(int i=0; i<200; ++i) 
    {


        mH = mapHyper2(p_sand);
        c+=mH;    
        if(c>seuil)break;
        p_sand+=r*(mH-seuil)*0.02;
    }
    c= 0.0;
    vec3 water_surface_normal;
    vec2 off=vec2(0.05,0.0);
    water_surface_normal=normalize(mapHyper(p_water)-vec3(mapHyper(p_water-off.xyy), mapHyper(p_water-off.yxy), mapHyper(p_water-off.yyx)));

    vec3 p_sand_underwater=s;
     vec3 r2 = refract((-p_water),water_surface_normal,1.333);
     for(int i=0; i<200; ++i) 
    {
        mH = mapHyper2(p_sand_underwater);
        c+=mH;    
        if(c>seuil)break;
        p_sand_underwater+=r2*(mH-seuil)*0.02;
    }

	vec3 final_color=vec3(0);
    float fresnel;
    vec3 reflection;

    vec3 zVect = vec3(0.0,0.0,1.0);
    float sand_water_dist = length(p_water)-length(p_sand);
    vec3 light_position = vec3(0.0,-0.0,-150.0);
    float power = 2.5;
    if(sand_water_dist<0.0)
    {
        // This part is for the water surface and the sand underwater rendering

        // Calculate the color of the water based on the water surface normal and the light direction
        vec3 water_color = mix(water_color_1,water_color_2,(abs(1.0+dot(water_surface_normal,s)*pow(dot(zVect,r),5.0))));
        vec3 light_direction = normalize(light_position);
        vec3 sand_underwater_normal=normalize(mapHyper2(p_sand_underwater)-vec3(mapHyper2(p_sand_underwater-off.xyy), mapHyper2(p_sand_underwater-off.yxy), mapHyper2(p_sand_underwater-off.yyx)));
        // Calculate the color of the sand underwater based on the light direction and the sand underwater normal
        vec3 sand_underwater_color = mix(sand_color_1 ,sand_color_2 ,vec3(dot(-light_direction,sand_underwater_normal)))*(1.0-clamp(length(light_position-p_water)/200.0,0.0,1.0))*power;
        // Calculate the final color by blending the water color and the sand underwater color based on the sand-water distance
        final_color = mix(water_color, sand_underwater_color*(1.0+sand_water_dist*0.1), clamp((1.0+sand_water_dist*0.1), 0.0, 1.0));
        fresnel = clamp(1.0 - dot(water_surface_normal, s), 0.05, 0.75);
        reflection = getSkyColor(abs(reflect(r,water_surface_normal)));
        // This line blends the final color with the reflection color based on the fresnel value multiplied by the x component of the reflection color.
        final_color = mix(final_color, reflection, (fresnel * reflection.x));
        if(sand_water_dist>-0.15){      
            // This line add a little edging where the sand and the water meet      
            final_color =mix(mix(final_color,final_color+reflection*0.5,reflection.x),final_color,-sand_water_dist*8.0);
        }
    }
    else{
        // This part is for the sand rendering
        vec3 light_direction = normalize(light_position);
        // Calculate the color of the sand based on the light direction and the sand normal
        vec3 sand_normal=normalize(mapHyper2(p_sand)-vec3(mapHyper2(p_sand-off.xyy), mapHyper2(p_sand-off.yxy), mapHyper2(p_sand-off.yyx)));
        final_color =mix(sand_color_1,sand_color_2,vec3(dot(-light_direction,sand_normal)))*(1.0-clamp(length(light_position-p_sand)/200.0,0.0,1.0))*power;
        // Comment the final_color line and uncomment this part to have a sandy result
        // vec3 color_temp =mix(sand_color_1,sand_color_2,vec3(dot(-light_direction,sand_normal)))*(1.0-clamp(length(light_position-p_sand)/200.0,0.0,1.0))*power;
        // p_sand += vec3(noise(p_sand*100.0),noise(p_sand*100.0),noise(p_sand*100.0))*10.0;
        // sand_normal=normalize(mapHyper2(p_sand)-vec3(mapHyper2(p_sand-off.xyy), mapHyper2(p_sand-off.yxy), mapHyper2(p_sand-off.yyx)));
        // reflection = getSkyColor(abs(reflect(r,sand_normal)));
        // final_color =mix(sand_color_1,sand_color_2+clamp(reflection*0.7*dot(-light_direction,sand_normal),0.0,2.0),vec3(dot(-light_direction,sand_normal)))*(1.0-clamp(length(light_position-p_sand)/200.0,0.0,1.0))*power;
        // final_color = mix(color_temp, final_color, color_temp);
        }
    float fog =  clamp((length(p_water-s)-fogSize)*fogCoef,0.0,1.0);
    final_color = mix(final_color,fog_color,fog);
	fragColor = vec4(final_color,1.0);
}
