// Fork of "better template" by ProfessorPixels. https://shadertoy.com/view/l3yGRV
// 2024-06-23 22:39:18

void mainImage(out vec4 O,vec2 F)
{
    O=T F/R);
}

#define rad 50.
vec3 h(vec3 c) {return c.z-c.z*c.y*clamp(2.-abs(mod(c.x/60.-vec3(0,2,4),6.)-3.),0.,1.);}
vec2 bounce(vec2 p,vec2 b){return 2.*min(mod(p,b),b-mod(p,b));}
void mainImage( out vec4 O, vec2 F )
{
    O = T (F+vec2(0,1))/R)+
        T (F-vec2(0,1))/R)+
        T (F+vec2(1,0))/R)+
        T (F-vec2(1,0))/R)+
        .6*T (F+vec2(1,1))/R)+
        .6*T (F-vec2(1,1))/R)+
        .6*T (F+vec2(1,-1))/R)+
        .6*T (F+vec2(-1,1))/R);
    O/=6.41;
    F-=rad+bounce(vec2(iTime*100.),iResolution.xy-rad-rad);
    if (length(F)<50. && abs(F.x)<rad && abs(F.y)<rad) O=vec4(h(vec3(iTime*10.,1,1)),1);
}
