vec3 palette(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b * cos(6.283185 * (c * t + d));
}

struct hp {
    float h;
    float l;
};

hp addhp(hp a, hp b) {
    float sh = a.h + b.h;
    float t = sh - a.h;
    float sl = (a.h - (sh - t)) + (b.h - t) + a.l + b.l;
    return hp(sh, sl);
}

hp subhp(hp a, hp b) {
    return addhp(a, hp(-b.h, -b.l));
}

hp mulhp(hp a, hp b) {
    float ph = a.h * b.h;
    float pl = a.h * b.l + a.l * b.h + a.l * b.l;
    return hp(ph, pl);
}

hp scalehp(hp a, float s) {
    return hp(a.h * s, a.l * s);
}

float sqrmag(hp r, hp i) {
    return r.h * r.h + 2.0 * r.h * r.l + r.l * r.l +
           i.h * i.h + 2.0 * i.h * i.l + i.l * i.l;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float mi = 5192.;


    vec2 scrcoord = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    hp cx = hp(0.3245, 0.0);
    hp cy = hp(0.052, 0.0);
    float zoom = pow(2.0, iTime);

    hp sc = hp(1.0 / zoom, 0.0);

    hp uvx = addhp(scalehp(hp(scrcoord.x, 0.0), sc.h), cx);
    hp uvy = addhp(scalehp(hp(scrcoord.y, 0.0), sc.h), cy);

    hp cr = uvx;
    hp ci = uvy;
    hp r = hp(0.0, 0.0);
    hp i = hp(0.0, 0.0);

    float r2 = 0.0;
    const float th = 4.0;
    float iter = 0.0;

    for (float j = 0.0; j < mi && r2 < th; j++) {
        hp r2_hp = mulhp(r, r);
        hp i2_hp = mulhp(i, i);
        hp temp = r;
        r = addhp(subhp(r2_hp, i2_hp), cr);
        hp ri = mulhp(temp, i);
        i = addhp(scalehp(ri, 2.0), ci);
        r2 = sqrmag(r, i);
        iter = j;
    }

    if (r2 < th) {
        fragColor = vec4(0.0);
    } else {
        float se = iter - log2(log2(sqrt(r2)));
        float ne = se / float(mi) * 500.;
        vec3 col = palette(ne, vec3(0.5), vec3(0.5), vec3(1.0), vec3(0.0, 0.333, 0.667));
        fragColor = vec4(col, 1.0);
    }
}
