void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec2 uvOffset = uv.xy*10. + vec2(0.,0.);
    vec3 rgbColOffset = vec3(0.0,2.,4.0);
    vec3 circle = 2.0 * length(uvOffset) - rgbColOffset - iTime;
    vec3 col = cos(circle) + sin(uvOffset.y + rgbColOffset - iTime)+ sin(uvOffset.x - rgbColOffset - iTime);

    // Output to screen
    fragColor = vec4(col,1.0);
}
