// 実装参考：https://thebookofshaders.com/07/?lan=jp
// 実装参考：https://30min-processing.hatenablog.com/entry/2017/04/19/000000
// https://trap.jp/post/142/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  // Normalized pixel coordinates (from 0 to 1)
  vec2 uv = fragCoord/iResolution.xy;

  float m = min(iResolution.x, iResolution.y);

  vec2 st = fragCoord/iResolution.xy;
  float pct = 0.0;
  vec2 toCenter = vec2(0.5)-st;
  pct = length(toCenter) + 0.3; // -0.1が半径にあたる部分

  vec4 tex = texture(iChannel0,fract(st*pct));
  fragColor = tex;

  // 円のぼやっと具合確認用
  // vec3 color = vec3(pct);
  // fragColor = vec4( color, 1.0 );
}
