void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(
        cos(-pow(uv.x - .5, 2.) * 50. + iTime + 100.), // R
        0.,  // G
        cos(-pow(-uv.x - .5, 2.) * 70. + iTime - 100.),  // B
    1.)

    +  cos(uv.x * 50. + iTime * 3.) * vec4(1., 0., 1., 0.);

}
