#define S 0.992
#define R 0.002
#define M 0.031

void mainImage(out vec4 o, in vec2 c) {
    c /= iResolution.xy;

    o = iFrame < 1 ? 
        c.xxxy / M: 
        texture(iChannel0, (c - 0.5) * mat2(S, -R, R, S) + 0.5);

    o += M * sin(o.yzwx);
}
