void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = ( u+u - R ) / R.y;              // normalized coordinates
    vec3 P = vec3( U, sqrt(1.-dot(U,U)) ),   // = point and normal on sphere
      // T = reflect( vec3(0,0,-1), N );     // reflected ray from infinity
         T =  vec3(0,0,-1) + 2.*P *P.z ;     // = I - 2. * dot(N, I) * N
#if 1                                        // environment: plane Z = 1

    P += ( 1. - P.z ) / T.z * T;             // ray from P to plane z = 1
 // P += (( sign(T) - P) / T ).z * T;        // variante: front + rear plane z = +-1
    O = texture( iChannel0, P.xy*.5+.5 );
 // O /= length(fwidth(P)) *R.y/4.;          // (empirical) Jacobian fading
 // R = vec2( length(vec2(dFdx(P.x),dFdy(P.x))), length(vec2(dFdx(P.y),dFdy(P.y))) )*R.y/2. , O /= R.x*R.y*.7; // correct Jacobian

#else                                        // environment: cubemap

    vec3 A = abs(T);
    int i =   A.z > max(A.x,A.y) ? 2         // plane id = main reflect direction 
            : A.y > max(A.x,A.z) ? 1
            :                      0;
    P += ( sign(T[i]) - P[i] ) / T[i] * T;  // ray from P to plane xi = sign(Ti)
//  P += (( sign(T) - P ) / T ) [i] * T;    // golfed
    O = texture( iChannel0, vec2( P[(i+1)%3], P[(i+2)%3]) *.5+.5 );
#endif   
}
