void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec4 c_empty = vec4(0.);
    vec4 c_wall = vec4(1.);
    vec4 c_origin = vec4(1., 0., 0., 0.);
    vec4 c_light = vec4(0., 0., 1., 0.);

    vec2 fixed_pos = (iResolution.xy * vec2(0.5));
    vec2 fixed_ofst = fragCoord - fixed_pos;

    vec2 origin = (iResolution.xy * vec2(0.5)) + sin(iTime * 1.0) * vec2(0.0, 250.0);

    origin = iMouse.xy;

    vec2 ofst = fragCoord - origin;
    float dist = distance(ofst, vec2(0.));
    vec2 dir = normalize(ofst);

    vec2 stepped = (fragCoord - (dir * 5.0)) / iResolution.xy;

    if (dist < 25.0) {
        fragColor = c_origin;
    } else if (sin(fixed_ofst.x / 25.0) * sin(fixed_ofst.y / 25.0) > 0.9) {
        fragColor = c_wall;
    } else if (texture(iChannel0, stepped) == c_light || texture(iChannel0, stepped) == c_origin) {
        fragColor = c_light;
    } else {
        fragColor = vec4(stepped / 518.0, 0., 1.);
        fragColor = c_empty;
    }
}
