void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.4,e,t=iTime; vec3 p,q=vec3(1,2,0),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.z-=4.;
    p*=rotate3D(t*.1,q);

    for(j=0.;j++<8.;)
        p-=mod(p,.01),
        p=abs(p*rotate3D(1.41,q)-.04)-.05,
        d=max(max(p.y,p.x), p.z),
        d-=dot(p,p)/1e4;

    e+=d;

}//o+=3./i;
o+=vec4(sqrt(p/d),1)*3./i;
}
