// Определение функции palette с использованием синусов и косинусов
vec3 palette(float value) {
    // Используем синус и косинус для генерации цвета
    float red = 0.1 * sin(value * 5.0 + iTime);   
    float green = 1.0 * sin(value * 10.0 + iTime);  
    float blue = 0.5 * cos(value * 5.0 + iTime);   
    return vec3(red, green, blue); 
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Нормализуем координаты фрагмента для диапазона [-1, 1]
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;
    vec3 finalColor = vec3(0.0);

    // Цикл для создания эффекта
    for (float i = 0.0; i < 3.0; i++) {

        uv = fract(uv * 2.0) - 0.5;


        float d = length(uv);

        // Используем функцию palette для получения цвета
        vec3 col = palette(length(uv) + iTime);

        // Создаем эффект волны
        d = sin(d * 8.0 + iTime) / 8.0;
        d = abs(d);

        // Изменяем интенсивность в зависимости от расстояния
        d = 0.02 / d;

        finalColor += col * d;
    }

    fragColor = vec4(finalColor, 1.0);
}
