void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float fny;

    float fny1 = sin(uv.x*50.0 + iTime*0.8)*0.3 + 0.5;
    float fny2 = sin(uv.x*(100.0 ) + iTime * 20.0)*0.1 + 0.8;
    float fny3 = sin(uv.x*100.0 - iTime* 22.0)*0.1 + 0.1;

    if ((uv.y >= fny1 - 0.02 && uv.y <= fny1 + 0.04) ||
        (uv.y >= fny2 - 0.02 && uv.y <= fny2 + 0.04) ||
        (uv.y >= fny3 - 0.02 && uv.y <= fny3 + 0.04))
        fny = 1.0;
    else 
        fny = 0.0;



    // Output to screen
    fragColor = vec4(fny, col);
}
