void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Background
    vec2 uvBackground = fragCoord / iResolution.xy;
    vec3 colorBackground = vec3(uvBackground.y, uvBackground.x, 1.0);

    fragColor = vec4(colorBackground, 1.0);

    // Ring Animation
    float time = iTime * 0.85; 
    float oscillation = 0.3 * sin(time) + 0.8; 

    // Ring
    vec2 uvRing = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    float d = length(uvRing);
    d = abs(d);
    float thickness = 0.15;
    float radius = oscillation;
    float ring = step(radius - thickness, d) - step(radius, d);
    vec3 colorRing = vec3(uvBackground.x, uvBackground.y, 1.0);

    fragColor = vec4(mix(colorBackground, colorRing, ring), 1.0);

}
