uint seed = 0u;

void setSeed(uint s) {
    seed = s;
}

uint hash(uint s) {
    s ^= 2747636419u;
    s *= 2654435769u;
    s ^= s >> 16;
    s *= 2654435769u;
    s ^= s >> 16;
    s *= 2654435769u;
    return s;
}

uint randI() {
    seed = hash(seed);
    return seed;
}

float rand() {
    uint rn = randI();
    rn %= 1000000000u;
    float rs = float(rn);
    rs /= 1000000000.0;
    return rs;
}

