void mainImage( out vec4 fragColor, in vec2 uv )
{
    if(mod(float(iFrame),1.)==0.){
    vec2 fragCoord=uv;

    float n=3.;
    uv.x = n*(uv.x-0.5*iResolution.x)/iResolution.y;
    uv.y = n*(uv.y-0.5*iResolution.y)/iResolution.y;
    fragCoord=uv;

    setNSeed();

    //float k=pow(10.,(1.5-0.5*cos(float(iFrame)/4800.)));
    float k=100.;
    float t=float(iFrame)/(1.*480.);
    float h=4.;
    vec3 col=vec3(0.);
    for(float i=1.;i<=k;i+=1.){
        float r1=rand();
        float r2=rand();

        float r3=rand();
        float r4=rand();
        vec2 p=vec2(floor(h*(0.25*cos(5.*t)+pow(r2,0.5)*cos(2.*3.1415*r1)))/h,floor(h*(0.25*sin(5.*t)+pow(r2,0.5)*sin(2.*3.1415*r1)))/h);
        col += (0.5 + 0.5*cos(iTime/1.+p.xyx+vec3(0,2,4)))*0.2/k*vec3(pow(length(uv-(p+vec2(r3/h,r4/h))),-2.)); 


    }
    // Output to screen
    fragColor = vec4(col,1.0);
    }else{
     fragColor = texture(iChannel0, (uv)/iResolution.xy);     
    }
}
