vec4 Circle (vec2 uv, float r) {
  float x = uv.x;
  float y = uv.y;

  float d = length(vec2(x, y)) - r;

  if (d < 0.0) {
      return vec4(0.0);
  }
  else {
   return vec4(1.0, 1.0, 1.0, 1.0);
  }
}

vec4 Ellips (vec2 uv, float r){
    float x = 0.6*uv.x;
    float y = uv.y;

    float d = length(vec2(x, y)) - r;
    if (d < 0.0) {
      return vec4(0.9, 0.9, 0.97, 1.0);
    }
    else {
      return vec4(0.0);
    }
}

void mainImage (out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec4 tube = Circle (uv, 0.48);
    if (tube.r > 0.0) {
        fragColor = tube - vec4(1.0, 1.0, 1.0, 0.0);
    }

    else {
        uv = fragCoord.xy / iResolution.xy;
        vec3 wave_color = vec3(0.4, 0.6, 1.0);

        uv  = -0.9 + 3.0 * uv;
        uv.y += 0.2;
        for (float i = 0.0; i < 5.0; i += 0.3) {
            uv.y += (0.02 * sin(uv.x + i/7.0 + 2.0*iTime));
            float wave_width = abs(1.0 / (700.0 * uv.y));
            wave_color += vec3(wave_width, wave_width, wave_width * 10.0);
            if (uv.y <= 0.2 + 0.02 * sin(uv.x + i/7.0 + 2.0*iTime )) {
                wave_color = vec3(0.3, 0.1, 0.8);
            }
        }

	fragColor = vec4(wave_color, 1.0);
    uv = fragCoord / iResolution.xy;
    if (uv.y >= 0.5){
        fragColor.b -= 0.3*uv.y;
    }
    fragColor.r -= 1.0/(100.0*uv.y);
    fragColor.g -= 1.0/(50.0*uv.y);
    fragColor.b -= 1.0/(100.0*uv.y);

    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec4 Cloud1 = Ellips (uv + vec2(0.2, -0.2), 0.05);
    if (Cloud1.b > 0.0) {
        fragColor = Cloud1;
    }
    vec4 Cloud2 = Ellips (uv + vec2(0.13, -0.25), 0.05);
    if (Cloud2.b > 0.0) {
        fragColor = Cloud2;
    }
    vec4 Cloud3 = Ellips (uv + vec2(0.07, -0.2), 0.04);
    if (Cloud3.b > 0.0) {
        fragColor = Cloud3;
    }
  }
}
