#define PI 3.14159265358979323846
#define TAU 6.28318530717958647692

mat2 rot( in float a ) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float rand( in float seed ) {
    return fract(sin(seed*573834.997));
}

float lerp_noise( in float seed ) {
    float i = floor(seed);
    float f = fract(seed);
    return mix(rand(i), rand(i+1.0), smoothstep(0.0, 1.0, f));
}

float noise( in float seed ) {
    float sum = 0.0;
    sum += lerp_noise(seed    )*0.5;
    sum += lerp_noise(seed*2.0)*0.25;
    sum += lerp_noise(seed*4.0)*0.125;
    return sum;
}

float sdf_box( in vec2 p, in vec2 b ) {
    vec2 q = abs(p) - b;
    return length(max(q, 0.0)) + min(max(q.x, q.y), 0.0);
}

float sdf_circle( in vec2 p, in float r ) {
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;
    uv.x += noise(iTime*0.4+584.2)*0.5;
    uv.y += noise(iTime);
    uv *= rot((noise(iTime*1.5)-0.3)*0.2);

    vec2 m = (iMouse.xy*2.0 - iResolution.xy)/iResolution.y;
    m.x += noise(iTime*0.4+584.2)*0.5;
    m.y += noise(iTime);
    m *= rot((noise(iTime*1.5)-0.3)*0.2);

    vec3 col = vec3(0.0);
    vec3 c = vec3(0.867,0.627,0.518);

    vec2 p = uv;
    vec2 q = uv;
    for (int i = 0; i < 5; i += 1) {
        col = mix(col, c, (1.0 - smoothstep(-0.01, 0.01, sdf_box(p, vec2(0.5, 0.2))) ));
        col = mix(col, c, (1.0 - smoothstep(-0.01, 0.01, sdf_box(q, vec2(0.5, 0.2))) ));

        p -= vec2(0.5, 0.0);
        p = p*rot(noise(iTime+float(i)*0.42)*PI)*2.0;
        p -= vec2(0.2, 0.0);
        q += vec2(0.5, 0.0);
        q = q*rot(-noise(iTime+float(i)*0.66+0.1)*PI)*2.0;
        q += vec2(0.2, 0.0);
        c.rb *= rot(0.06);
        c.rg *= rot(0.02);
    }

    col += vec3(1.0 - smoothstep(-0.01, 0.01, sdf_circle(uv - vec2(0.3, 0.0), 0.1)));
    col += vec3(1.0 - smoothstep(-0.01, 0.01, sdf_circle(uv + vec2(0.3, 0.0), 0.1)));
    col += vec3(0.867,0.408,0.133)*(1.0 - smoothstep(-0.01, 0.01, sdf_circle(uv - m, 0.1)));

    vec2 to_mouse = normalize(m - vec2(0.3, 0.0));
    col *= smoothstep(-0.01, 0.01, sdf_circle(uv - vec2(0.3, 0.0) - to_mouse*0.03, 0.06));
    col *= smoothstep(-0.01, 0.01, sdf_circle(uv + vec2(0.3, 0.0) - to_mouse*0.03, 0.06));

    fragColor = vec4(col, 1.0);
}
