void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0 - iResolution.xy) / iResolution.y;

    vec2 uv2 = fract(uv*1.1)-0.5;

    vec2 a = cos(uv*(2.0+(mod(iTime/10.0, 0.2)-0.1)*(mod(iTime/10.0, 0.2)-0.1)) + iTime/10.0);
    vec2 b = sin(uv+iTime);
    vec2 c = cos(uv2*(2.0+(mod(iTime/10.0, 0.2)-0.1)*(mod(iTime/10.0, 0.2)-0.1)) + iTime/10.0);
    vec2 d = sin(uv2+iTime);

    vec3 A = vec3(a.x - b.y, b.x * b.y, a.y*sin(iTime));
    vec3 B = vec3(c.y - d.x, d.x * d.y, c.x*sin(iTime*0.7));


    fragColor = vec4(sin(A+iTime)*0.8+sin(B)*0.2, 1.0);
}
