float triangle(float x) {
    return abs(2.*fract(x)-1.);
}

float getVal(vec2 coord) {
    int q = (
        int((coord.x - coord.y) + iTime * 80.0 + iMouse.x * 10.0)
        &
        int((coord.y          ) + iTime * 80.0 + iMouse.y * 10.0)
    );
    return float(sin(float(q)) > (0.80 + triangle(0.9+iTime/4.0)/5.0));
    //return float(fract(float(q)*0.123456789) > (0.88 + triangle(iTime/3.0)/10.0));
    //return float(fract(float(q)*0.918273645) > (0.88 + triangle(iTime/3.0)/10.0));
}

float kernel[9] = float[](
    1.0, 8.0, 1.0,
    8.0, 64.0, 8.0,
    1.0, 8.0, 1.0
);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    float s = 0.0;
    int index = 0;
    for (int x = -1; x <= 1; x++) {
        for (int y = -1; y <= 1; y++) {
            s += getVal(fragCoord + vec2(x, y)) * kernel[index];
            index++;
        }
    }

    s /= (
        kernel[0] + kernel[1] + kernel[2] +
        kernel[3] + kernel[4] + kernel[5] +
        kernel[6] + kernel[7] + kernel[8]
    ) * 0.9;

    fragColor = vec4(s, s / 1.5, s / 0.2, 1.0);
}
