#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
 #define UIF (1.0 / float(0xffffffffU))
vec4 hash44(uvec4 q)
{
	q *= uvec4(1597334673U, 3812015801U, 2798796415U, 1979697957U);
	q = (q.x ^ q.y ^ q.z ^ q.w)*uvec4(1597334673U, 3812015801U, 2798796415U, 1979697957U);
	return vec4(q) * UIF;
}
#define pi 3.141592659

vec2 ln (vec2 v) {
	return vec2(log(length(v)), atan(v.x,v.y));
}
vec2 mul (vec2 a, vec2 b) {
	return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
}
vec2 f (vec2 v) {
    for (int i = 0; i < 51; i++) {
        v = ln(v)-.19;
        v = mul(v,v);
    }
    return vec2(.25,.05)*max(v,0.);
}
