vec2 maurerRose(float n,float k){
    return vec2(sin(k),cos(k))*(sin(k*n));
}
float line(vec2 p1, vec2 p2, float width, vec2 uv)
{
	float dist = distance(p1, p2);
	float dist_uv = distance(p1, uv);

	return 1.0 - floor(1.0 - (0.001 * width) + distance (mix(p1, p2, clamp(dist_uv / dist, 0.0, 1.0)),  uv));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n=1.5; //change me
    float d=0.9; // change me
    float k=0.;
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y*2.;

    vec3 col = vec3(0,0,0);
    for(int j = 0; j < 720; j++)
    {
        col.x+=max(line(maurerRose(n,k), maurerRose(n,k+d), 1.5, uv), 0.);
        k+=d;
    }

    fragColor = vec4(col,1.0);
}
