    bool colorUpperLeft = evenX && evenY;
    bool colorLowerRight = !evenX && !evenY;

    // Color the pixels accordingly
    if (colorUpperLeft || colorLowerRight) {
        fragColor = vec4(texColor.rgb, 1.0); // Color the upper left and lower right pixels
