void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy);
    uv -= 0.5;
    uv *= 0.65;
    uv.x /= 0.5;




    vec4 color_a = vec4(0.9686, 0.6431, 0.0, 1.0);
    vec4 color_b = vec4(0.0, 0.0, 0.0588, 1.0);
    vec4 color_b2 = vec4(0.0431, 0.0431, 0.5804, 1.0);
    float t = 0.0;
    for (float i; i < 10.0 ; i++)
    {
        float k = 0.001;
        k = 0.008;
        float scale = 10.0;

        float radius = 0.2 + 0.01*i;

        float torus = k/abs(length(uv)-radius - 0.02*pow(sin(2.0*(scale*uv.x+iTime+0.2*i)),2.0) ) ;

        t += torus;
    }

    vec4 c = mix(color_a, color_b, uv.x+0.5);

    t = t*uv.x;

    c = mix(c, color_b2, t);
    fragColor = vec4(c);
}
