void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col=0.;
    for(float j=-1.;j<=1.;j++){
        vec2 uv = vec2(4,1)*(fragCoord+vec2(j/3.,0.))/iResolution.xy;

        float V = 1.;

        float x=0.5;
        for(int i=0;i<300;i++){
            x=x*uv.x*(1.-x);
            if(i>150 && abs(uv.y-x)<(1./iResolution.y)){
                V=0.;
                break;}}
        col+=V;}
    fragColor = vec4(vec3(col/3.),1.0);
}
