#define rot(a)  mat2(cos(a+vec4(0,11,33,0)))
#define t mod(iTime,60.)
float sc; vec4 cc;
float df(vec4 p) {  
    p.xz *= rot(t/3.);
    p = cos(p/7.); 
    sc=length(p);
    cc =  p*sc ;
    float d = clamp(dot(p,p),1e-3,1e4);
    return (sqrt(d/sc)) * log(d);
}
void mainImage( out vec4 O, vec2 U) {        
    O *= 0.;
    float td;
    vec2 R = iResolution.xy;
    vec4 D = normalize( vec4( U+U-R, R.y, R.y) ) * 60.,
         p = vec4(0,0,4.*sin(t),0);  
    for (float d,i ; ++i<100.; )
        d = df(p)/40.,
        p += d * D, 
        td += d,
        O +=  .02*cos(cc/2.+vec4(1,2,3,0)) / exp(i*i*i*i*d*d/1e4) ;
    O = exp(-td*td/5.)*(1.-exp(-O*O)); 
}
