float d(vec2 uv){
    float N = 6.; 
    float R=1.;
    float r=0.32;
    float aoff=0.;
    vec2 olduv = uv;
    float dd;
    vec2 nearest;
    for(float iter=0.; iter<5.; iter++){
        dd=1000.;
        for(float i=0.; i<N; i++){
            float a = 6.28*i/N+0.2*iTime+aoff;
            vec2 center=(R-r)*vec2(cos(a),sin(a));
            float newdd = length(uv-center);
            if(newdd<dd){
                dd=newdd;
                nearest=center;
            }

        }

        uv=uv-nearest;
        uv/=r;
        aoff+=0.1*iTime;      
    }

        return dd;
}


vec3 render(vec2 uv){
    vec3 col;
    if(d(uv)<.35){
        col=vec3(1);
    }
    else{
        col=vec3(d(uv)/5.);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv=fragCoord/iResolution.y;
    vec2 middle = iResolution.xy/(2.*iResolution.y);
    uv-=middle;
    uv*=3.;
    vec3 col=render(uv);
    col*=render(uv+vec2(0.5*cos(0.28*iTime),0.35*sin(0.227*iTime)));
    col*= 0.5 + (0.45+0.03*cos(0.3*iTime))*cos(iTime+sin(uv.xyx*0.42)+vec3(0,2,4));
    fragColor=vec4(col,1.0);
}
