#define PI (atan(1.)*4.)
#define H(h)(cos((h)*6.3+vec3(10,23,21))*.5+.5)
#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)

float happy_star(vec2 uv, float anim)
{
    uv = abs(uv);
    vec2 pos = min(uv.xy/uv.yx, anim);
    float p = (2.0 - pos.x - pos.y);
    return (2.0+p*(p*p-1.5)) / (uv.x+uv.y);      
}
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);

    	vec2 uv=C.xy/iResolution.xy-.5;
    vec3 p,r=iResolution,

    d=normalize(vec3((C-.5*r.xy)/r.y,1));
      float anim = sin(iTime * 12.0) * 0.1 + 1.0;  // anim between 0.9 - 1.1 

    for(float i=0.,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(0.1,0.2,1.),H(log(s)*.3),.6)*.02*exp(-.3*i*i*e)
    )
    {
        p=g*d+vec3(0,0,iTime*.0);

        p.z+=iTime;
        p=atan(cos(p*PI/3.))/PI*3.;
  p=R(p, vec3(0,1.,0), iTime);
        s=5.;
        for(int i;i++<6;)

            p=abs(p-vec3(1.0,1.0,1.0))-vec3(1.1,1.3,1),
            s*=e=2.2/clamp(dot(p,p),0.5,2.3),
            p=abs(p)*e;
        g+=e=length(p.xz)/s;
    }
      uv *= 2.0 * ( cos(iTime * 2.0) -2.5); // scale

    O*= vec4(happy_star(uv, anim) * vec3(0.35,0.2,1.15)*1., 1.0);

}
