void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;

    // prevents any stretching
    uv.x *= iResolution.x / iResolution.y;

    // calculates the length from the center
    float d = length(uv);

    // gradient 1
    vec3 col1 = vec3(uv.y, 2.0, uv.x);

    //gradient 2
    vec3 col2 = vec3(1.0, uv.x, uv.y);

    //mixes them together using cos and time
    vec3 gradient = mix(col1, col2, cos(iTime) * 5.0 + 0.2);

    // makes the repeating + shrinking rings
    d = sin(d * 4. + iTime)/4.;
    d = abs(d);
    //creates the glowing effecr by grabbing the inverse of d
    d= 0.002 / d;

    // creates a faded circle
    d = smoothstep(0.0, 0.1, d);

    //applies the fading gradients to the
    gradient *= d;

    fragColor = vec4(gradient, 1.0);
}
