#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy-.20;

    for(float i=8.;i<10.;i++){
        uv.x=abs(sin(uv.x*8.+iTime));
        uv*=rot(iTime*i+i);
    }

    uv.x=uv.x+.2*sin(iTime);

    vec2 center = vec2(.5,.5);
    float d = distance(uv,center);

    fragColor = vec4(10,
        sin(iTime*100. + uv.y*8.)*.8+.8,
        sin(iTime*8. + uv.x*1.)*.2+.2,
        0.1);
}
