void mainImage( out vec4 O, in vec2 I )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 v = (I/iResolution.xy-vec2(0.5, 0.))*iTime*4.;
    float N = 20.;
    float a = 0.;
    for (float i = 0.; i < N; i++) {
        vec2 w = v-vec2(i-N/2., 0);
        a += sin(sqrt(dot(w,w)) * N)/N;
    }    
    O = vec4(a,0.,-a,1.);
}
