/*

Blackjack!
I have yet to see someone attempt this in shadertoy, so I reckon I'd be the first!

As part of writing this, I came up with a better way of shuffling cards on the GPU.
(Or more specifically shadertoy).
If you're interested it's in Common, search for "Hash based shuffle".

Actually most of the drawing, backend and layout logic is in Common.
The games logic is housed in `Buffer A`.

Enjoy!

*/


vec2 getCardOffset(uint index, uint num)
{
    uint d = max(1u, min(5u, num));
    return vec2(float(index % 5u) * 1.1 + float(5u - d) * 0.5, float(index / 5u) * 0.2);
}

vec4 drawDancingDeck(vec4 back, vec2 uv, Layout l, float eventTimer)
{
    if (back.w < 1.0)
    {

        float t0 = eventTimer * -0.1;

        const float timescale = 0.01;
        const float yscale = 0.25;
        const float xscale = 0.5;
        const float freq = 6.0;
        const float pi = 3.14159265359;
        const float halfpi = 1.57079632679;
        const float threehalfspi = 4.71238898038;

        // The dance animation is:
        // [
        //      0.5  * sin(6 * t),
        //      0.25 * cos(t)
        // ]
        //
        // Where t is:
        //     even: max(0, t0 - id * 0.01) - pi * 0.5
        //     odd:  1.5 * pi - max(0, t0 - id * 0.01)

        vec2 dStart = uv - l.deckCardStartUv;

        // Early out if we're outside the renderable region.
        if ((dStart.y < -yscale) || (dStart.x < -xscale) || (dStart.y > (yscale + l.cardLocalDim.y)) ||
            (dStart.x > (xscale + l.cardLocalDim.x))

        )
        {
            return back;
        }

        // Calc visible on screen (based upon animation)
        // t0 - x * 0.01 - pi <= 0
        uint startid = uint(clamp((t0 - pi) / timescale, 0.0, 53.0));

        // t0 - x * 0.01 >= 0
        // adding 1 card bias + ensure first card
        // is always visible
        uint endid = uint(clamp(t0 / timescale + 1.0, 1.0, 53.0));

        // TODO:
        // We should be able to infer
        // what cards would potentially
        // be under this pixel by
        // using acos with y (or something)

        // Range of cards that are valid for processing.
        // uvec2 odd = uvec2(startid | 1u, endid   | 1u);
        // uvec2 even = uvec2(startid + (startid & 1u), endid  + (endid & 1u));

        // Precalculate these, just to make math faster later on.
        vec2 danceCardBaseUv = bboxSpace(dStart, l.cardLocalUv);
        vec2 danceCardOffsetScale = vec2(1.0) / l.cardLocalDim;

        for (uint i = startid; i < endid; ++i)
        {
            uint card = FETCH_SC_STATE(1u + i);
            float t = max(0.0, t0 - float(i) * 0.01);

            // draw even
            if ((i & 1u) == 1u)
            {
                t = t - halfpi;
            }

            // draw odd
            else
            {
                t = threehalfspi - t;
            }

            vec2 anim = vec2(sin(t * freq) * xscale, cos(t) * yscale);
            back = drawCard(back, danceCardBaseUv - anim * danceCardOffsetScale, card);
        }
    }
    return back;
}

float rand4neonbg(vec2 co) 
{
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec3 neonChar(vec2 uv, float time) 
{
    float r = rand4neonbg(uv);
    float char = float(r > 0.9);
    float brightness = smoothstep(0.9, 1.0, sin(time * (r * 5.0 + 0.5) + uv.y * 10.0));
    return vec3(0.0, char * brightness, 0.0);
}

float drawLetter(vec2 uv, int letter) {
    uint[5] letterData;

    switch (letter) 
    {
        case 0: // W
            letterData = uint[5](0x11u, 0x11u, 0x15u, 0x15u, 0x0Au);
            break;
        case 1: // O
            letterData = uint[5](0x0Eu, 0x11u, 0x11u, 0x11u, 0x0Eu);
            break;
        case 2: // N
            letterData = uint[5](0x19u, 0x19u, 0x15u, 0x13u, 0x11u);
            break;
        case 3: // L
            letterData = uint[5](0x10u, 0x10u, 0x10u, 0x10u, 0x1Fu);
            break;
        case 4: // S
            letterData = uint[5](0x0Eu, 0x10u, 0x0Eu, 0x01u, 0x1Eu);
            break;
        case 5: // T
            letterData = uint[5](0x1Fu, 0x04u, 0x04u, 0x04u, 0x04u);
            break;
        default:
            return 0.0;
    }

    ivec2 pixel = ivec2(floor(uv * vec2(5, 5)));
    uint row = letterData[4 - pixel.y];
    uint bit = (row >> (4 - pixel.x)) & 1u;
    return float(bit);
}

void drawGameResult(vec2 uv, int playerState, inout vec4 fragColor)
{
    if (playerState != GAME_STATE_WON && playerState != GAME_STATE_LOST) 
    {
        return;
    }

    vec2 center = vec2(playerState == GAME_STATE_WON ? 0.16 : 0.26, 0.5);
    vec2 size = vec2(0.5, 0.2);
    vec2 bottomLeft = center - size * 0.5;
    vec2 topRight = center + size * 0.5;

    if (uv.x >= bottomLeft.x && uv.x <= topRight.x && uv.y >= bottomLeft.y && uv.y <= topRight.y)
    {
        // Background
        vec3 bgColor = (playerState == GAME_STATE_WON) ? vec3(0.0, 0.2, 0.0) : vec3(0.2, 0.0, 0.0);
        vec2 localUv = (uv - bottomLeft) / size;
        float edge = smoothstep(0.0, 0.05, min(min(localUv.x, 1.0 - localUv.x), min(localUv.y, 1.0 - localUv.y)));
        bgColor = mix(bgColor * 1.5, bgColor, edge);

        // Text
        vec2 textUv = (uv - bottomLeft) / (size / vec2(4.0, 1.0));
        int charIndex = int(floor(textUv.x));
        vec2 charUv = fract(textUv);

        float v = 0.0;
        if (charIndex >= 0 && charIndex < 4) {
            int letterIndex;
            if (playerState == GAME_STATE_WON) {
                // WON
                int[4] chars = int[4](-1, 0, 1, 2);
                letterIndex = chars[charIndex];
            } else {
                // LOST
                int[4] chars = int[4](3, 1, 4, 5);
                letterIndex = chars[charIndex];
            }
            v = drawLetter(charUv, letterIndex);
        }

        vec3 textColor = (playerState == GAME_STATE_WON) ? vec3(0.2, 1.0, 0.2) : vec3(1.0, 0.2, 0.2);
        vec3 glowColor = textColor * 1.5;

        float glow = smoothstep(0.4, 0.5, v);
        vec3 finalColor = mix(bgColor, textColor, v);
        finalColor += glowColor * pow(glow, 3.0) * 0.5;  // Add glow effect

        fragColor = vec4(finalColor, 1.0);
    }
}


void drawPlayArea(vec2 uv, Layout l, inout vec4 fragColor)
{

    int gameState = GAME_STATE_WAITING;
    int playerState = 0;
    int dealerState = 0;
    float eventTimer = 0.0;

    {
        vec4 gameStateRaw = FETCH_GLOBAL_STATE(GLOBAL_STATE_GAME_X);
        gameState = int(gameStateRaw.x);
        playerState = int(gameStateRaw.y);
        dealerState = int(gameStateRaw.z);
        eventTimer = gameStateRaw.w / EVENT_TIMER_DEAL_AMOUNT;
    }

    // Draw scores and such
    if (bboxTest(uv, l.playerScoreBarUv) || bboxTest(uv, l.dealerScoreBarUv))
    {
        fragColor = vec4(0.1, 0.1, 0.1, 1.0);

        uvec4 totalScores = uvec4(FETCH_GLOBAL_STATE(GLOBAL_STATE_TOTAL_SCORE_X));
        uvec4 scores = uvec4(FETCH_GLOBAL_STATE(GLOBAL_STATE_SCORE_X));

        uint toEncode = 0xffffffffu;
        vec4 encodeBbox = vec4(0.);
        vec3 renderCol = vec3(0.9);
        bool center = true;

        if (bboxTest(uv, l.playerTotalScoreUv))
        {
            toEncode = totalScores.x;
            encodeBbox = l.playerTotalScoreUv;
            center = false;

            if (playerState == GAME_STATE_WON)
            {
                renderCol = vec3(0.0, 1.0, 0.0);
            }
        }
        else if (bboxTest(uv, l.dealerTotalScoreUv))
        {
            toEncode = totalScores.y;
            encodeBbox = l.dealerTotalScoreUv;
            center = false;

            if (dealerState == GAME_STATE_WON)
            {
                renderCol = vec3(0.0, 1.0, 0.0);
            }
        }
        else if (bboxTest(uv, l.playerScoreUv))
        {
            uint playerScore = calcHandValue(scores.x, scores.y);

            toEncode = playerScore;
            encodeBbox = l.playerScoreUv;
            if (gameState == GAME_STATE_END)
            {
                if (playerState == GAME_STATE_WON)
                {
                    renderCol = vec3(0.0, 1.0, 0.0);
                }
                else if (playerState == GAME_STATE_LOST)
                {
                    renderCol = vec3(1.0, 0.0, 0.0);
                }
            }
        }
        // Don't render the dealers score until after the game is done
        else if ((gameState == GAME_STATE_END) && bboxTest(uv, l.dealerScoreUv))
        {
            uint dealerScore = calcHandValue(scores.z, scores.w);

            toEncode = dealerScore;
            encodeBbox = l.dealerScoreUv;
            if (dealerState == GAME_STATE_WON)
            {
                renderCol = vec3(0.0, 1.0, 0.0);
            }
            else if (dealerState == GAME_STATE_LOST)
            {
                renderCol = vec3(1.0, 0.0, 0.0);
            }
        }

        if (toEncode != 0xffffffffu)
        {
            uint scoreEnc = simpleEncodeDigits(toEncode, center);
            uint bitSigned = sampleEncodedNumber(scoreEnc, bboxSpace(uv, encodeBbox));
            if (bitSigned != 0u)
            {
                fragColor.xyz = renderCol;
            }
        }
    }


    // Render player cards
    {
        bool playerCardsMoving = gameState == GAME_STATE_DEAL_P_CARD;
        uint numPlayerCards = FETCH_PC_STATE(0);

        if (playerCardsMoving)
        {
            // peek into the future!
            uint index = FETCH_SC_STATE(0);
            uint card = FETCH_SC_STATE(1u + index);
            vec2 off = mix(l.playerCardStartUv + getCardOffset(0u, numPlayerCards + 1u) * l.cardLocalDim,
                           l.deckCardStartUv, eventTimer);
            vec2 drawUv = bboxSpace(uv - off, l.cardLocalUv);
            fragColor = drawCard(fragColor, drawUv, card);
        }

        for (uint ix = 0u; ix < numPlayerCards; ++ix)
        {
            // draw backwards
            uint v = numPlayerCards - ix - 1u;
            uint card = FETCH_PC_STATE(1u + v);

            vec2 off = getCardOffset(ix, numPlayerCards);
            if (playerCardsMoving)
            {
                off = mix(getCardOffset(ix + 1u, numPlayerCards + 1u), off, eventTimer);
            }

            if (playerState == GAME_STATE_WON)
            {
                float s = min(1.0, -eventTimer);
                off.y += sin(float(ix) + iTime * 10.0) * 0.5 * s;
                off.x += cos(float(ix) + iTime * 5.0) * 0.1 * s;
            }

            vec2 drawUv = bboxSpace(uv - l.playerCardStartUv - off * l.cardLocalDim, l.cardLocalUv);
            fragColor = drawCard(fragColor, drawUv, card);
        }
    }

    // Render dealers cards
    {
        uint numDealerCards = FETCH_DC_STATE(0);
        if (gameState == GAME_STATE_END)
        {
            for (uint ix = 0u; ix < numDealerCards; ++ix)
            {
                // draw backwards
                uint v = numDealerCards - ix - 1u;
                uint card = FETCH_DC_STATE(1u + v);
                vec2 off = getCardOffset(ix, numDealerCards);
                if (dealerState == GAME_STATE_WON)
                {
                    float s = min(1.0, -eventTimer);
                    off.y += sin(float(ix) + iTime * 10.0) * 0.5 * s;
                    off.x += cos(float(ix) + iTime * 5.0) * 0.1 * s;
                }

                vec2 drawUv = bboxSpace(uv - l.dealerCardStartUv - off * l.cardLocalDim, l.cardLocalUv);
                fragColor = drawCard(fragColor, drawUv, card);
            }
        }
        else
        {
            bool dealerCardsMoving = gameState == GAME_STATE_DEAL_D_CARD;
            if (dealerCardsMoving)
            {
                vec2 off = mix(l.dealerCardStartUv + getCardOffset(0u, numDealerCards + 1u) * l.cardLocalDim,
                               l.deckCardStartUv, eventTimer);
                vec2 drawUv = bboxSpace(uv - off, l.cardLocalUv);
                fragColor = drawCardBack(fragColor, drawUv);
            }

            for (uint ix = 0u; ix < numDealerCards; ++ix)
            {
                // draw backwards
                vec2 off = getCardOffset(ix, numDealerCards);
                if (dealerCardsMoving)
                {
                    off = mix(getCardOffset(ix + 1u, numDealerCards + 1u), off, eventTimer);
                }
                vec2 drawUv = bboxSpace(uv - l.dealerCardStartUv - off * l.cardLocalDim, l.cardLocalUv);
                fragColor = drawCardBack(fragColor, drawUv);
            }
        }
    }

    if (gameState == GAME_STATE_END)
    {
        fragColor = drawDancingDeck(fragColor, uv, l, eventTimer);
    }
    else // Draw the deck
    {

        fragColor = drawCardBack(fragColor, bboxSpace(uv - l.deckCardStartUv, l.cardLocalUv));
    }

    drawGameResult(uv, playerState, fragColor);

    // Original BG
    /*if(fragColor.w <= 1.0)
    {
        float k = sin(uv.y * 20.0 + cos(uv.x * 10.));
        float b = float(k > 0.0);
        vec3 c = mix(vec3(0.1, 0.2, 0.3),
                     vec3(0.1, 0.2, 0.4),
                     b);
        fragColor = mergeUnder(vec4(c, 1.0), fragColor);
    }*/

    // static neon background
    /*if (fragColor.w <= 1.0) 
    {
        vec2 center = vec2(0.5);
        float dist = length(uv - center);
        vec3 bgColor = mix(vec3(0.05, 0.05, 0.1), vec3(0.1, 0.1, 0.2), smoothstep(0.0, 0.5, dist));
        float gridLine = max(
            smoothstep(0.98, 1.0, sin(uv.x * 20.0)),
            smoothstep(0.98, 1.0, sin(uv.y * 20.0))
        );
        bgColor = mix(bgColor, vec3(0.2, 0.8, 1.0), gridLine * 0.3);
        fragColor = mergeUnder(vec4(bgColor, 1.0), fragColor);
    }*/

    // Neon bits background
    if (fragColor.w <= 1.0)
    {
        vec2 neonUv = uv * vec2(30.0, 20.0);
        vec2 neonCell = floor(neonUv);

        vec2 neonLocal = fract(neonUv);
        vec3 neonColor = neonChar(neonCell, iTime);
        float fade = smoothstep(0.0, 0.2, neonLocal.y) * smoothstep(1.0, 0.8, neonLocal.y);
        neonColor *= fade;

        // cool glow effect
        float glow = max(0.0, 1.0 - length(neonLocal - 0.5) * 2.0);
        neonColor += vec3(0.0, glow * 0.1, 0.0);

        // blend the neon effect with black
        vec3 bgColor = vec3(0.02, 0.05, 0.03);
        neonColor = mix(bgColor, neonColor, 0.8);

        fragColor = mergeUnder(vec4(neonColor, 1.0), fragColor);
    }


}

void getButtonStateCols(uint state, out vec3 fg, out vec3 bg, out vec3 text)
{
    if (state == BUTTON_STATE_NONE)
    {
        fg = vec3(0.2, 0.4, 0.8);
        bg = vec3(0.05, 0.05, 0.1);
        text = vec3(1.0, 1.0, 1.0);
    }
    else if ((state == BUTTON_STATE_DOWN) || (state == BUTTON_STATE_CLIK))
    {
        fg = vec3(1.0, 0.2, 0.4);
        bg = vec3(0.1, 0.02, 0.05);
        text = vec3(1.0, 1.0, 0.8);
    }
    else
    {
        fg = vec3(0.3, 0.3, 0.4);
        bg = vec3(0.1, 0.1, 0.15);
        text = vec3(0.7, 0.7, 0.7);
    }
}

void drawButtons(vec2 uv, Layout l, inout vec4 fragColor)
{
    uvec4 buttonStates = uvec4(FETCH_GLOBAL_STATE(GLOBAL_STATE_BUTTONS_X));
    vec3 fg, bg, text;
    vec2 innerUv = vec2(0.0);
    uint sampleCoord = 0xd0d0d0d0u;

    if (bboxTest(uv, l.newGameUv))
    {
        getButtonStateCols(buttonStates.x, fg, bg, text);
        vec2 buttonLocal = bboxSpace(uv, l.newGameUv);

        if (l.mobileButtons)
        {
            innerUv = buttonLocal + vec2(0.05, 0.0);
            sampleCoord = 0xbeu;
        }
        else
        {
            buttonLocal = buttonLocal * vec2(4.0, 1.0) - vec2(1.5 / 4.0, -0.05);
            if ((buttonLocal.x > 0.0) && (buttonLocal.x < 3.0))
            {
                uint idx = uint(buttonLocal.x);
                innerUv = vec2(fract(buttonLocal.x), buttonLocal.y);
                sampleCoord = 0xd0a7b5beu;
                sampleCoord >>= (8u * idx);
            }
        }
    }
    else if (bboxTest(uv, l.hitMeUv))
    {
        getButtonStateCols(buttonStates.y, fg, bg, text);
        vec2 buttonLocal = bboxSpace(uv, l.hitMeUv);

        if (l.mobileButtons)
        {
            innerUv = buttonLocal + vec2(0.05, 0.0);
            sampleCoord = 0xb8u;
        }
        else
        {
            buttonLocal = buttonLocal * vec2(4.0, 1.0) - vec2(1.5 / 4.0, -0.05);
            if ((buttonLocal.x > 0.0) && (buttonLocal.x < 3.0))
            {
                uint idx = uint(buttonLocal.x);
                innerUv = vec2(fract(buttonLocal.x), buttonLocal.y);
                sampleCoord = 0xd0a4b9b8u;
                sampleCoord >>= (8u * idx);
            }
        }
    }
    else if (bboxTest(uv, l.standUv))
    {
        getButtonStateCols(buttonStates.z, fg, bg, text);
        vec2 buttonLocal = bboxSpace(uv, l.standUv);

        if (l.mobileButtons)
        {
            innerUv = buttonLocal + vec2(0.05, 0.0);
            sampleCoord = 0xa3u;
        }
        else
        {
            buttonLocal = buttonLocal * vec2(4.0, 1.0) - vec2(0.0, -0.05);
            if ((buttonLocal.x > 0.0) && (buttonLocal.x < 4.0))
            {
                uint idx = uint(buttonLocal.x);
                innerUv = vec2(fract(buttonLocal.x), buttonLocal.y);
                sampleCoord = 0xb4bea4a3u;
                sampleCoord >>= (8u * idx);
            }
        }
    }

    if (bboxTest(innerUv, vec2(0., 1.).xxyy))
    {
        sampleCoord &= 0xffu;
        vec2 p = (vec2(sampleCoord & 15u, sampleCoord >> 4) + innerUv * 1.1 - 0.1) * .0625;
        float v = texture(iChannel1, p).x;

        // Button background
        float edgeDist = min(min(innerUv.x, 1.0 - innerUv.x), min(innerUv.y, 1.0 - innerUv.y));
        float edge = smoothstep(0.0, 0.05, edgeDist);
        vec3 buttonColor = mix(fg, bg, edge);

        // Text with glow effect
        float glow = smoothstep(0.4, 0.5, v);
        vec3 textColor = mix(buttonColor, text, glow);
        textColor += text * pow(glow, 3.0) * 0.5; // Add extra glow

        fragColor.xyz = textColor;
    }
    else
    {
        fragColor.xyz = bg;
    }
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    Layout l = processLayout(iResolution.xy);
    uv *= l.normUvParams;

    fragColor = vec4(0.0);

    // Menu inputs
    if (bboxTest(uv, l.controlsBackUv))
    {
        fragColor.xyz = vec3(0.2);
        drawButtons(uv, l, fragColor);
    }

    // Game area
    else
    {
        drawPlayArea(uv, l, fragColor);
    }
}

// Quality settings
#define ENABLE_AA 1

#define USE_PARALLEL_SHUFFLE 1

// Incredibly unlikely, but it is technically
// possible when doing a shuffle for a hash collision
// to occur, causing two cards to have the same value.
#define PREVENT_SHUFFLE_COLLISION 0

#define SUITE_HEART 0u
#define SUITE_DIAMOND 1u
#define SUITE_SPADE 2u
#define SUITE_CLUB 3u

#define CARD_ASPECT_RATIO 1.3968253968253967
#define CARD_INV_ASPECT_RATIO 0.7159090909090909

// GL = Y starts at the bottom
// DX = Y starts at the top
#ifndef Y_STARTS_AT_BOTTOM
#define Y_STARTS_AT_BOTTOM 1
#endif

uint simpleHash32(uvec3 Seed)
{
    uint hx = (0xb543c3a6u ^ Seed.x);
    uint hy = (0x526f94e2u ^ Seed.y);
    uint hxy = hx * hy;
    uint hz0 = 0x53c5ca59u ^ (hxy >> 5u);
    uint hz1 = (0x74743c1bu ^ Seed.z);
    uint h = hz0 * hz1;
    return h;
}

// Merge A under B
vec4 mergeUnder(vec4 A, vec4 B)
{
    return A * (1.0 - B.w) + B;
}

bool bboxTest(vec2 uv, vec4 bbox)
{
    return (uv.x > bbox.x) && (uv.x < bbox.z) && (uv.y > bbox.y) && (uv.y < bbox.w);
}

vec2 bboxSpace(vec2 uv, vec4 bbox)
{
    return (uv - bbox.xy) / (bbox.zw - bbox.xy);
}

// Unpack a card, given it's id.
void unpackCard(uint cardId, out uint value, out uint type)
{
    value = cardId % 13u + 1u;
    type = cardId / 13u;
}

// Calculate the value of a hand.
// This takes into account the duality
// of aces, so they are 11, unless
// doing so would overflow 21.
uint calcHandValue(uint valueWithoutAces, uint numAces)
{
    uint value = valueWithoutAces + numAces;

    // There can only be one max value ace.
    // 11 + 11 = 22, which would bust.
    if ((value <= 11u) && (numAces > 0u))
    {
        value += 10u;
    }

    return value;
}

/*
Added just for fun
*/

vec3 neonColor(uint suite) 
{
    if (suite == SUITE_HEART || suite == SUITE_DIAMOND) 
    {
        return vec3(1.0, 0.2, 0.4); // Neon pink for hearts and diamonds
    } 
    else
    {
        return vec3(0.2, 0.8, 1.0); // Neon blue for spades and clubs
    }
}

vec3 neonGlow(vec3 color, float intensity) 
{
    return color * intensity;
}

float neonEdge(float distance, float thickness, float glow)
{
    float line = smoothstep(thickness, 0.0, abs(distance));
    float halo = smoothstep(glow, thickness, abs(distance));
    return line + halo * 0.5;
}


float drawSuiteShapeRaw(vec2 uv, uint type)
{
    uv = uv * 2.0 - 1.0;
    uv.y *= CARD_ASPECT_RATIO;

    if (type == SUITE_SPADE)
    {
        uv.y -= 0.1;
        float d = abs(uv.x) * uv.y * -1.1 - abs(uv.x) - uv.y * uv.y + 0.5;

        uv *= 2.0;
        uv.y += 1.5;
        float d2 = abs(uv.x) * uv.y * -1.1 - abs(uv.x) - uv.y * uv.y + 0.5;
        return max(d, d2);
    }
    if (type == SUITE_HEART)
    {
        return abs(uv.x) * uv.y - dot(uv, uv) + 0.4;
    }
    if (type == SUITE_DIAMOND)
    {
        return dot(-abs(uv), vec2(1.5, 1.2)) + 1.0;
    }

    // if(type == SUITE_CLUB)
    {

        // I won't lie to you, most of the clubs generation is
        // yolo at best, logic and reason went out the window
        // in favour of eye-balling the output and playing with
        // numbers almost randomly until it looked ok.

        // left / right balls
        float y1 = uv.y;
        y1 *= -y1;
        vec2 x12 = uv.xx + vec2(-0.45, 0.45);
        x12 *= -x12;

        // middle ball
        float y2 = uv.y - 0.5;
        y2 *= -y2;

        float x2 = -uv.x * uv.x;

        // Three balls merged:
        //    -x² + -(y - 0.5)²
        //    -(x - 0.45)² + -y²
        //    -(y + 0.45)² + -y²
        float d = max(x2 + y2, max(x12.x + y1, x12.y + y1)) + 0.1;

        // Does the weird-ish middle shape.
        // don't ask
        float dex = 15.0 * x2 + (y1 * 1.5) + 0.7;
        d = max(d, d - dex * max(x12.x + y1, x12.y + y1));

        // Rounded tail (basically a spade)
        uv.y += 0.4;
        uv *= 2.0;
        float d3 = abs(uv.x) * uv.y * -1.3 - abs(uv.x) - uv.y * uv.y + 0.5;

        d = max(d, d3);
        return d;
    }
}

float drawSuiteShape(vec2 uv, uint type)
{
    // Early out, corner is always not signed.
    if ((uv.x == 0.) && (uv.y == 0.))
    {
        return 0.;
    }

#if !ENABLE_AA
    // Clubs does whack crap stuff OOB
    // AA filters this later
    if ((max(abs(uv.x - 0.5), abs(uv.y - 0.5)) > 0.4))
    {
        return 0.0;
    }
#endif // !ENABLE_AA

    float d = drawSuiteShapeRaw(uv, type);

#if ENABLE_AA
    float aa = fwidth(d) * 0.5;
    // Remove artefacts at uv borders, when AA is enabled
    if ((max(abs(uv.x - 0.5), abs(uv.y - 0.5)) > 0.4))
    {
        return 0.0;
    }
    if (aa > 1.0)
    {
        return 0.0;
    }
    d = smoothstep(-aa, aa, d);
#else  // ENABLE_AA
    d = float(d > 0.0);
#endif // ENABLE_AA
    return d;
}

vec2 calcCardInnerSuiteUvAdj(vec2 uv, uint value, float row, float col)
{
    // no pattern for ace, 2 or 3
    if (value < 4u)
    {
        return vec2(0.);
    }

    // 6 - 8 have a middle (so need a bit of spacing)
    if ((value >= 6u) && (value <= 8u) && (row >= 1.0) && (row <= 2.0))
    {
        uv.y = uv.y - 1.5 + row;
        return uv;
    }

    // flip after halfway point
    if (row < 2.0)
    {
        uv.y = 1.0 - uv.y;
    }

    // 9 - 10 have everything
    if ((value == 9u) || (value == 10u))
    {
        return uv;
    }

    // JQK have only left-up and right-down patterns.
    if (value >= 11u)
    {
        if (((col == 2.0) && (row == 0.0)) || ((col == 0.0) && (row == 3.0)))
        {
            return uv;
        }
        return vec2(0.0);
    }

    // 4 - 10 have top and bottom
    if ((row == 0.0) || (row == 3.0))
    {
        return uv;
    }

    return vec2(0.);
}

vec2 calcCardInnerSuiteUvMid(vec2 uv, uint value, float row)
{
    // no pattern for 4, 6 or JQK
    if ((value >= 11u) || (value == 4u) || (value == 6u))
    {
        return vec2(0.);
    }

    // ace, 3, 5 and 9 have a middle (so need a bit of spacing)
    if (((value & 1u) == 1u) && (value != 7u))
    {

        if ((row >= 1.0) && (row <= 2.0))
        {
            uv.y = uv.y - 1.5 + row;
            return uv;
        }

        // ace, 5 and 9 have nothing but middle
        if (value != 3u)
        {
            return vec2(0.);
        }

        // 3 has up and down, aligned to 4
        if (row < 2.0)
        {
            uv.y = 1.0 - uv.y;
        }
        return uv;
    }

    // The rest of these have an annoying shift
    uv.y = row + uv.y + 0.5;
    row = floor(uv.y);
    uv.y = fract(uv.y);

    if (row == 3.0)
    {
        return uv;
    }
    if ((value != 7u) && (row == 1.0))
    {
        return uv;
    }

    return vec2(0.);
}

vec2 calcCardInnerSuiteUv(vec2 uv, uint value)
{
    vec2 result = vec2(0.);

    uv.x = uv.x * 3.0;
    float col = floor(uv.x);
    uv.x = fract(uv.x);

    uv.y *= 4.0;
    float row = floor(uv.y);
    uv.y = fract(uv.y);

    // left and right coloumn are always identical (except for JQK)
    if (col != 1.0)
    {
        result = calcCardInnerSuiteUvAdj(uv, value, row, col);
    }
    else
    {
        result = calcCardInnerSuiteUvMid(uv, value, row);
    }

    // aspect ratio
    result.x = result.x * 4.0 / 3.0 - 0.5 / 3.0;

    return result;
}

// Adapted from: `Encoding and drawing numbers`
//               https://www.shadertoy.com/view/dtjXWK
//
//
// .###. ..#.. .###. ##### #...# ##### .#### ##### .###. .###.
// #..## .##.. #...# ....# #...# #.... #.... ....# #...# #...#
// #.#.# ..#.. ...#. ..##. #...# ####. ####. ...#. .###. #...#
// ##..# ..#.. ..#.. ....# .#### ....# #...# ..#.. #...# .####
// #...# ..#.. .#... #...# ....# ....# #...# ..#.. #...# ....#
// .###. .###. ##### .###. ....# ####. .###. ..#.. .###. .###.
//
// #..## .#### .###. #...# .###. .....
// #.#.# ....# #...# #..#. #...# .....
// #.#.# ....# #...# ###.. #...# .....
// #.#.# ....# #...# #..#. ##### .....
// #.#.# #...# #..#. #...# #...# .....
// #.##. .###. .##.# #...# #...# .....

uint numberPixels[16] = uint[16](
#if !Y_STARTS_AT_BOTTOM
    0x1d19d72eu, 0x1c4210c4u, 0x3e22222eu, 0x1d18321fu, 0x210f4631u, 0x1f083c3fu, 0x1d18bc3eu, 0x0842221fu, 0x1d18ba2eu,
    0x1d0f462eu, 0x1b5ad6b9u, 0x1d18421eu, 0x2c98c62eu, 0x23149d31u, 0x231fc62eu, 0x00000000u
#else
    0x1d9ace2eu, 0x0862108eu, 0x1d14105fu, 0x3f06422eu, 0x2318fa10u, 0x3e17c20fu, 0x3c17c62eu, 0x3f041084u, 0x1d17462eu,
    0x1d18fa0eu, 0x335ad6adu, 0x3d08422eu, 0x1d18c536u, 0x2293a631u, 0x1d18fe31u, 0x00000000u
#endif
);

struct RepBuffer
{
    uint data;
    uint index;
};

RepBuffer RepBuffer_init()
{
    RepBuffer repBuffer;
    repBuffer.data = 0u;
    repBuffer.index = 0u;
    return repBuffer;
}

void RepBuffer_push(inout RepBuffer repBuffer, uint value)
{
    repBuffer.data |= ((~value) & 15u) << (4u * repBuffer.index++);
}

uint RepBuffer_remainingSpace(RepBuffer repBuffer)
{
    return 8u - repBuffer.index;
}

uint RepBuffer_get(RepBuffer repBuffer)
{
    return ~repBuffer.data;
}

void RepBuffer_rev(inout RepBuffer repBuffer)
{
    uint i = repBuffer.index;
    uint k = 0u;

    for (uint i = 1u; i <= repBuffer.index; ++i)
    {
        k |= ((repBuffer.data >> (4u * (repBuffer.index - i))) & 15u) << (4u * (i - 1u));
    }

    repBuffer.data = k;
}

void RepBuffer_center(inout RepBuffer repBuffer)
{
    repBuffer.data <<= (4u * ((8u - repBuffer.index) >> 1));
}

uint simpleEncodeDigits(uint n, bool center)
{
    RepBuffer repBuffer = RepBuffer_init();

    n = min(n, 99999999u);

    if (n == 0u)
    {
        RepBuffer_push(repBuffer, 0u);
    }
    while (n > 0u)
    {
        RepBuffer_push(repBuffer, n % 10u);
        n /= 10u;
    }
    RepBuffer_rev(repBuffer);

    if (center)
    {
        RepBuffer_center(repBuffer);
    }

    return RepBuffer_get(repBuffer);
}

uint sampleEncodedDigit(uint encodedDigit, vec2 uv)
{
    if (uv.x < 0. || uv.y < 0. || uv.x >= 1. || uv.y >= 1.)
        return 0u;
    uvec2 coord = uvec2(uv * vec2(5., 6.));
    return (numberPixels[encodedDigit] >> (coord.y * 5u + coord.x)) & 1u;
}

// 8 character variant
uint sampleEncodedNumber(uint encodedNumber, vec2 uv)
{
    // Extract the digit ID by scaling the uv.x value by 8 and clipping
    // the relevant 4 bits.
    uv.x *= 8.0;
    uint encodedDigit = (encodedNumber >> (uint(uv.x) * 4u)) & 0xfu;

    // Put the U in between then [0, 1.2] range, the extra 0.2 is add a
    // logical 1px padding.
    // (6/5, where 5 is the number of pixels on the x axis)
    uv.x = fract(uv.x) * 1.2;

    return sampleEncodedDigit(encodedDigit, uv);
}


float drawCardType(vec2 uv, uint value)
{
    // Ace gets a little redirection
    if (value == 1u)
    {
        value = 14u;
    }
    float v = float(sampleEncodedDigit(value, uv));
    return v;
}

vec4 drawCard(vec4 back, vec2 uv, uint value, uint type) 
{
    if (bboxTest(uv, vec2(0.0, 1.0).xxyy) && (back.w < 1.0)) 
    {
        vec2 ndcRd = uv * 2.0 - 1.0;
        float edgeDf = 1.0 - dot(ndcRd, ndcRd);

        vec3 neonCol = neonColor(type);
        float edge = neonEdge(edgeDf, 0.05, 0.2);
        vec3 cardColor = mix(vec3(0.05), neonCol, edge);

        vec2 typeUv = vec2(0.0);
        vec2 valueUv = vec2(0.0);

        if ((min(uv.x, uv.y) > 0.15) && (max(uv.x, uv.y) < 0.85)) 
        {
            typeUv = calcCardInnerSuiteUv((uv - 0.15) * 1.4285714285714286, value);
            if (value >= 11u) {
                if ((min(uv.x, uv.y) > 0.4) && (max(uv.x, uv.y) < 0.6))
                {
                    valueUv = (uv - 0.4) * 5.;
                }
            }
        } else {
            vec2 outerUv = uv;
            if (outerUv.y < 0.5) {
                outerUv = 1.0 - outerUv;
            }

            if ((outerUv.x > 0.05) && (outerUv.x < 0.15)) 
            {
                if ((outerUv.y > 0.85) && (outerUv.y < 0.95)) {
                    valueUv = (outerUv - vec2(0.05, 0.85)) / (0.15 - 0.05);
                } else if ((outerUv.y > 0.75) && (outerUv.y < 0.85)) {
                    typeUv = (outerUv - vec2(0.05, 0.75)) / (0.15 - 0.05);
                }
            }
        }

        if ((valueUv.x != 0.0) && (valueUv.y != 0.0)) 
        {
            float valueEdge = drawCardType(valueUv, value);
            cardColor = mix(cardColor, neonGlow(neonCol, 1.5), valueEdge);
        } else {
            float suiteEdge = drawSuiteShape(typeUv, type);
            cardColor = mix(cardColor, neonGlow(neonCol, 1.5), suiteEdge);
        }

        vec4 outValue = vec4(cardColor, 1.0);
        back = mergeUnder(outValue, back);
    }

    return back;
}

vec4 drawCard(vec4 back, vec2 uv, uint cardId)
{
    uint value = 0u;
    uint type = 0u;
    unpackCard(cardId, value, type);
    return drawCard(back, uv, value, type);
}

vec4 drawCardBack(vec4 back, vec2 uv)
{
    if (bboxTest(uv, vec2(0.0, 1.0).xxyy) && (back.w < 1.0)) 
    {
        vec2 ndcRd = uv * 2.0 - 1.0;
        float edgeDf = 1.0 - dot(ndcRd, ndcRd);

        vec3 neonCol = vec3(0.2, 0.8, 1.0); // Neon blue for card back
        float edge = neonEdge(edgeDf, 0.05, 0.2);
        vec3 cardColor = mix(vec3(0.05), neonCol, edge);

        float pattern = abs(cos((uv.x * 2.0 - 1.0) * (uv.y * 2.0 - 1.0) * 25.0));
        cardColor = mix(cardColor, neonGlow(neonCol, 1.5), smoothstep(0.7, 1.0, pattern));

        vec4 outValue = vec4(cardColor, 1.0);
        back = mergeUnder(outValue, back);
    }

    return back;
}

// Hash based shuffle
// ------------------
//
// Trying to use Fisher-Yates, in the
// context of outputting an id to
// a pixel, would result in each thread
// having to store 52 (or 13) registers.
// Which would be and excessive amount of
// register pressure.
// Unless you process a value each frame,
// which would then take a boring amount
// of time to resolve.
//
// Instead this takes a different approach.
// Each element calculates a hash for itself,
// then counts how many other elements have
// a smaller hash.
// This results in it's, new id.
//
// It has the same ALU cost as Fisher-Yates,
// since a random number needs to be generated
// for each element, but reduces register use.

uint hash_shuffle_id(uint id, uvec3 seed)
{
    uint h = simpleHash32(uvec3(id ^ seed.x, seed.yz));

#if PREVENT_SHUFFLE_COLLISION
    h <<= 6;
    h |= id;
#endif // PREVENT_SHUFFLE_COLLISION

    return h;
}

uint hash_shuffle(uint id, uint num, uvec3 seed)
{
    uint h = hash_shuffle_id(id, seed);
    uint idx = 0u;
    for (uint i = 0u; i < num; ++i)
    {
        if (hash_shuffle_id(i, seed) < h)
        {
            ++idx;
        }
    }
    return idx;
}

uint shuffle_52(uint i, uvec3 seed)
{
    return hash_shuffle(i, 52u, seed);
}

// Two part parallel shuffle
// -------------------------
//
// First pass is 13 short.
// Second pass is 4 wide.
//
// Derived from the factors of 52
// (number of playing cards).
//
// Logically you can think of this as:
// 1. Shuffling in suit in parallel.
// 2. Shuffling suit type in parallel.
//
// This does add a bit of determinism,
// every 13th card will be a different
// type of suite, however practically,
// this probably doesn't matter.

uint shuffle_13(uint i, uvec3 seed)
{
    return hash_shuffle(i, 13u, seed);
}

// Fisher-Yates, for 4 elements
// with the result stored in a single
// uint to minimize register pressure.
uint shuf4_swap(uint v, uint i, uint j)
{
    i <<= 1;
    j <<= 1;
    uint x = ((v >> i) ^ (v >> j)) & 3u;
    v = ((x << i) | (x << j)) ^ v;
    return v;
}

// Out index: 2:2:2:2
uint shuffle_4_packed(uint seed)
{
    uint v = 228u;

    v = shuf4_swap(v, 0u, seed & 3u);
    v = shuf4_swap(v, 1u, (seed >> 2) % 3u + 1u);
    v = shuf4_swap(v, 2u, 2u + ((seed >> 7) & 1u));
    return v;
}

uint shuffle_4(uint i, uvec3 seed)
{
    uint v = shuffle_4_packed(simpleHash32(seed));
    uint idx = (v >> (i << 1)) & 3u;
    return idx;
}

uint shuffle_13_4(uint i, uvec3 seed)
{
    // Resolve whichever suite we're swapping with
    uint shuf4_id = i / 13u;
    uint shuf13_id = i % 13u;
    uint outer = shuffle_4(shuf4_id, seed + shuf13_id ^ seed.x);

    // Use that suites seed, so we are swapping as part
    // of it's array.
    uint inner = shuffle_13(shuf13_id, seed ^ (outer * 1313u));

    return inner + outer * 13u;
}

uint shuffle_deck(uint i, uvec3 seed)
{
#if USE_PARALLEL_SHUFFLE
    return shuffle_13_4(i, seed);
#else  // USE_PARALLEL_SHUFFLE
    return shuffle_52(i, seed);
#endif // USE_PARALLEL_SHUFFLE
}

// Test for ensuring shuffling looks
// correct, pink is true shuffle,
// cyan is split-parallel shuffle
vec3 shuffle_test(vec2 uv, uvec3 seed)
{
    uv.y *= 2.0;

    uint i = uint(uv.x * 52.0);
    uint j = uint(fract(uv.y) * 52.0);

    uint idx = 0u;
    vec3 col = vec3(0.);

    // True shuffle
    if (uv.y >= 1.0)
    {
        col = vec3(1., 0., 1.);
        idx = shuffle_52(i, seed);
    }

    // Parallel shuffle
    else
    {
        col = vec3(0., 1., 1.);
        idx = shuffle_13_4(i, seed);
    }

    float J = float(idx >= j);
    return col * J;
}

// Process the layout, given the aspect
// ratio and what have you, make sure
// buttons, where text goes and where
// cards go makes sense.

vec2 normalisedUvParams(vec2 res)
{
    vec2 result = vec2(1.);
    if (res.x >= res.y)
    {
        result.x = res.x / res.y;
    }
    else
    {
        result.y = res.y / res.x;
    }
    return result;
}

struct Layout
{
    vec2 normUvParams;
    vec4 controlsBackUv;

    bool mobileButtons;
    vec4 newGameUv;
    vec4 hitMeUv;
    vec4 standUv;
    vec4 playAreaUv;

    vec4 playerScoreBarUv;
    vec4 playerTotalScoreUv;
    vec4 playerScoreUv;

    vec4 dealerScoreBarUv;
    vec4 dealerTotalScoreUv;
    vec4 dealerScoreUv;

    vec4 cardLocalUv;
    vec2 cardLocalDim;
    vec2 playerCardStartUv;
    vec2 dealerCardStartUv;
    vec2 deckCardStartUv;
};

Layout processLayout(vec2 res)
{
    Layout l;

    l.normUvParams = normalisedUvParams(res);

    // Interface on the right
    if (res.x >= res.y)
    {
        l.controlsBackUv = vec4(l.normUvParams.x - 0.3, 0., l.normUvParams.x, 1.0);

        vec2 btnUBase = vec2(l.normUvParams.x - 0.25, l.normUvParams.x - 0.05);
        float btnAspect = 1.0 / 3.0;
        float btnHeight = (btnUBase.y - btnUBase.x) * btnAspect;
        float btnPadding = btnHeight * 2.5;

        l.mobileButtons = false;
        l.newGameUv = vec4(btnUBase.x, 0.9 - btnHeight, btnUBase.y, 0.9);

        l.hitMeUv = vec4(btnUBase.x, 0.5 - btnHeight - btnPadding * 0.5, btnUBase.y, 0.5 - btnPadding * 0.5);

        l.standUv = vec4(btnUBase.x, 0.5 - btnHeight + btnPadding * 0.5, btnUBase.y, 0.5 + btnPadding * 0.5);

        l.playAreaUv = vec4(0., 0., l.controlsBackUv.x, 1.0);
    }

    // At the bottom (phone fullscreen)
    else
    {
        l.controlsBackUv = vec4(0., 0., 1.0, 0.2);

        float buttonPadding = 0.05;
        vec2 buttonSize = l.controlsBackUv.ww - 2.0 * buttonPadding;

        l.mobileButtons = true;
        l.newGameUv = vec4(buttonPadding, buttonPadding, buttonSize + buttonPadding);

        l.hitMeUv =
            vec4(0.5 - buttonSize.x * 0.5, buttonPadding, 0.5 + buttonSize.x * 0.5, buttonSize.y + buttonPadding);

        l.standUv =
            vec4(1.0 - buttonSize.x - buttonPadding, buttonPadding, 1.0 - buttonPadding, buttonSize.y + buttonPadding);

        l.playAreaUv = vec4(0.0, l.controlsBackUv.w, 1.0, l.normUvParams.y);
    }

    // Player score
    l.playerScoreBarUv =
        vec4(l.playAreaUv.xy, l.playAreaUv.z, l.playAreaUv.y + (l.playAreaUv.w - l.playAreaUv.y) * 0.05);

    float scoreBarWidth = l.playerScoreBarUv.z - l.playerScoreBarUv.x;
    float scoreBarHeight = l.playerScoreBarUv.w - l.playerScoreBarUv.y;
    float scorePadding = min(scoreBarWidth, scoreBarHeight) * 0.2;

    l.playerScoreUv.xy = l.playerScoreBarUv.xy + scorePadding;
    l.playerScoreUv.z = l.playerScoreUv.x + (scoreBarHeight - 2.0 * scorePadding) * 6.0;
    l.playerScoreUv.w = l.playerScoreBarUv.w - scorePadding;

    l.playerTotalScoreUv = l.playerScoreUv;

    // Center score
    float scoreOffset = (scoreBarWidth - (l.playerScoreUv.z - l.playerScoreUv.x)) * 0.5;
    l.playerScoreUv.xz += scoreOffset;

    // Dealer score
    float dealerScoreYDelta = l.playAreaUv.w - scoreBarHeight - l.playerScoreBarUv.y;
    l.dealerScoreBarUv = l.playerScoreBarUv + vec4(0.0, dealerScoreYDelta, 0.0, dealerScoreYDelta);
    l.dealerScoreUv = l.playerScoreUv + vec4(0.0, dealerScoreYDelta, 0.0, dealerScoreYDelta);

    l.dealerTotalScoreUv = l.playerTotalScoreUv + vec4(0.0, dealerScoreYDelta, 0.0, dealerScoreYDelta);

    vec4 cardAreaUv = vec4(l.playerScoreBarUv.xw, l.dealerScoreBarUv.zy);

    // Practically allocating more than we need.
    const float numCardsWidth = 7.0;
    const float numCardsHeight = 6.0;

    vec2 cardAreaUvDim = cardAreaUv.zw - cardAreaUv.xy;

    l.cardLocalUv.xy = vec2(0.0);

    // This logic is kind of broken.
    if ((CARD_INV_ASPECT_RATIO * numCardsWidth / cardAreaUvDim.x) >
        (CARD_ASPECT_RATIO * numCardsHeight / cardAreaUvDim.y))
    {
        l.cardLocalUv.w = cardAreaUvDim.x * CARD_ASPECT_RATIO / numCardsWidth;
        l.cardLocalUv.z = l.cardLocalUv.w * CARD_INV_ASPECT_RATIO;
    }
    else
    {
        l.cardLocalUv.z = cardAreaUvDim.y * CARD_INV_ASPECT_RATIO / numCardsHeight;
        l.cardLocalUv.w = l.cardLocalUv.z * CARD_ASPECT_RATIO;
    }

    l.cardLocalDim = l.cardLocalUv.zw;
    l.cardLocalUv.zw += l.cardLocalUv.xy;

    // Center cards
    float pdCardStartU = (cardAreaUv.z + cardAreaUv.x) * 0.5 - 1.1 * 5.0 * l.cardLocalDim.x * 0.5;

    l.playerCardStartUv = cardAreaUv.xy + vec2(pdCardStartU, l.cardLocalDim.y * 0.1);
    l.dealerCardStartUv = cardAreaUv.xw + vec2(pdCardStartU, l.cardLocalDim.y * -1.1);
    l.deckCardStartUv = (cardAreaUv.xy + cardAreaUv.zw - l.cardLocalDim) * 0.5;

    return l;
}

#define EVENT_TIMER_DEAL_AMOUNT 0.3

#define GAME_STATE_MAX_X 53
#define GAME_STATE_MAX_Y 5

#define GAME_STATE_GLOBAL_STATE_Y 0
#define GLOBAL_STATE_GAME_X 0
#define GLOBAL_STATE_TIME_X 1
