#define GAME_STATE_PLAYER_CARDS_Y 2
#define GAME_STATE_DEALER_CARDS_Y 3

#define FETCH_GAME_STATE(X, Y) (texelFetch(iChannel0, ivec2(int(X), int(Y)), 0))
#define FETCH_GLOBAL_STATE(X) FETCH_GAME_STATE(X, GAME_STATE_GLOBAL_STATE_Y)
#define FETCH_SC_STATE(X) uint(FETCH_GAME_STATE(X, GAME_STATE_SHUFFLED_CARDS_Y).x)
#define FETCH_PC_STATE(X) uint(FETCH_GAME_STATE(X, GAME_STATE_PLAYER_CARDS_Y).x)
#define FETCH_DC_STATE(X) uint(FETCH_GAME_STATE(X, GAME_STATE_DEALER_CARDS_Y).x)

#define GAME_STATE_WAITING 0
#define GAME_STATE_END 1
#define GAME_STATE_DEAL_P_CARD 2
#define GAME_STATE_DEAL_D_CARD 3

#define GAME_STATE_PLAYING 0
#define GAME_STATE_STANDING 1
#define GAME_STATE_LOST 2
#define GAME_STATE_WON 3
#define GAME_STATE_DRAW 4

#define BUTTON_STATE_NONE 0u
#define BUTTON_STATE_DOWN 1u
#define BUTTON_STATE_CLIK 2u
#define BUTTON_STATE_DISABLED 3u

uint updateButtonState(vec2 mouseUv, bool down, vec4 bbox, uint prevState)
{
    if (prevState == BUTTON_STATE_DISABLED)
    {
        return BUTTON_STATE_DISABLED;
    }
    if (prevState == BUTTON_STATE_CLIK)
    {
        return BUTTON_STATE_NONE;
    }

    // if previous state was down, if it's no longer down
    if (!down && (prevState == BUTTON_STATE_DOWN))
    {
        return BUTTON_STATE_CLIK;
    }
    if (down && bboxTest(mouseUv, bbox))
    {
        return BUTTON_STATE_DOWN;
    }
    return BUTTON_STATE_NONE;
}

// This buffer controls all regular game logic.

void mainImage(out vec4 fragColor, in vec2 fcoord)
{
    fragColor = vec4(0.0, 0.0, 0.0, 0.0);

    ivec2 coord = ivec2(fcoord);
    if ((coord.x > GAME_STATE_MAX_X) || (coord.y > GAME_STATE_MAX_Y))
    {
        return;
    }

    Layout l = processLayout(iResolution.xy);

    int gameState = GAME_STATE_WAITING;
    int playerState = 0;
    int dealerState = 0;
    float eventTimer = 0.0;

    {
        vec4 gameStateRaw = FETCH_GLOBAL_STATE(GLOBAL_STATE_GAME_X);
        gameState = int(gameStateRaw.x);
        playerState = int(gameStateRaw.y);
        dealerState = int(gameStateRaw.z);
        eventTimer = gameStateRaw.w - iTimeDelta;
    }

    int ogGameState = gameState;

    // Make sure this is always kept in sync
    if (playerState == GAME_STATE_LOST)
    {
        dealerState = GAME_STATE_WON;
        gameState = GAME_STATE_END;
    }
    if (dealerState == GAME_STATE_LOST)
    {
        playerState = GAME_STATE_WON;
        gameState = GAME_STATE_END;
    }

    uvec4 scores = uvec4(FETCH_GLOBAL_STATE(GLOBAL_STATE_SCORE_X));

    // Handle input
    uvec4 buttonStates = uvec4(FETCH_GLOBAL_STATE(GLOBAL_STATE_BUTTONS_X));
    bool mouseDown = (iMouse.z > 0.0) || (iMouse.w > 0.0);
    vec2 mouseUv = (iMouse.xy / iResolution.xy) * l.normUvParams;

    // Disable buttons when events are taking place or game has ended
    if ((eventTimer > 0.0) || (gameState == GAME_STATE_END) || (playerState == GAME_STATE_STANDING))
    {
        buttonStates.y = BUTTON_STATE_DISABLED;
        buttonStates.z = BUTTON_STATE_DISABLED;
    }
    else
    {
        if (buttonStates.y == BUTTON_STATE_DISABLED)
        {
            buttonStates.y = BUTTON_STATE_NONE;
        }
        if (buttonStates.z == BUTTON_STATE_DISABLED)
        {
            buttonStates.z = BUTTON_STATE_NONE;
        }
    }

    // new game
    buttonStates.x = updateButtonState(mouseUv, mouseDown, l.newGameUv, buttonStates.x);
    // hitme
    buttonStates.y = updateButtonState(mouseUv, mouseDown, l.hitMeUv, buttonStates.y);
    // stand
    buttonStates.z = updateButtonState(mouseUv, mouseDown, l.standUv, buttonStates.z);

    // Timer updates
    float prevTime = FETCH_GLOBAL_STATE(GLOBAL_STATE_TIME_X).x;
    float curTime = prevTime + iTimeDelta;

    bool bNewGame = (iFrame == 0) || (buttonStates.x == BUTTON_STATE_CLIK);

    if (bNewGame)
    {
        // Create a shuffled deck.
        if (coord.y == GAME_STATE_SHUFFLED_CARDS_Y)
        {
            // Zero the current card index.
            if (coord.x == 0)
            {
                return;
            }

            uvec3 seed = floatBitsToUint(iDate.yzw) ^ uint(iFrame);
            // We _could_, so each pixel stores 4 values, but that would make
            // indexing a lot more complicated.
            uint shuffled = shuffle_deck(uint(coord.x - 1), seed);
            fragColor.x = float(shuffled);
            return;
        }

        // Zero everything, that isn't the global state
        else if (coord.y != GAME_STATE_GLOBAL_STATE_Y)
        {
            return;
        }

        // Reset global timer.
        curTime = 0.0;
        prevTime = 0.0;

        // Deal a card to the player
        gameState = GAME_STATE_DEAL_P_CARD;
        playerState = GAME_STATE_PLAYING;
        dealerState = GAME_STATE_PLAYING;
        eventTimer = EVENT_TIMER_DEAL_AMOUNT;

        scores = uvec4(0u);
    }

    // As a starting point, copy back whatever is
    // currently stored.
    fragColor = texelFetch(iChannel0, coord, 0);

    bool dealPlayerNewCard = false;
    bool dealDealerNewCard = false;

    if (gameState == GAME_STATE_DEAL_P_CARD)
    {
        if (playerState == GAME_STATE_STANDING)
        {
            gameState = GAME_STATE_DEAL_D_CARD;
            eventTimer = EVENT_TIMER_DEAL_AMOUNT;
        }
        else if (eventTimer <= 0.0)
        {
            dealPlayerNewCard = true;
            gameState = GAME_STATE_DEAL_D_CARD;
            eventTimer = EVENT_TIMER_DEAL_AMOUNT;
        }
    }
    else if (gameState == GAME_STATE_DEAL_D_CARD)
    {
        if (dealerState == GAME_STATE_STANDING)
        {
            gameState = GAME_STATE_WAITING;
        }
        else if (eventTimer <= 0.0)
        {
            dealDealerNewCard = true;
            gameState = GAME_STATE_WAITING;

            // Keep giving the dealer cards
            if (playerState == GAME_STATE_STANDING)
            {
                gameState = GAME_STATE_DEAL_D_CARD;
                eventTimer = EVENT_TIMER_DEAL_AMOUNT;
            }
        }
    }

    // Someone is getting a card.
    if (dealPlayerNewCard || dealDealerNewCard)
    {
        uint index = FETCH_SC_STATE(0);
        uint card = FETCH_SC_STATE(1u + index);
        int outY = dealPlayerNewCard ? GAME_STATE_PLAYER_CARDS_Y : GAME_STATE_DEALER_CARDS_Y;

        // Increment the pointer
        if ((coord.y == GAME_STATE_SHUFFLED_CARDS_Y) && (coord.x == 0))
        {
            fragColor.x = float(index + 1u);
        }

        // Write back new card
        else
        {
            if (coord.y == outY)
            {
                uint pindex = uint(FETCH_GAME_STATE(0, outY).x);
                if (coord.x == 0)
                {
                    fragColor.x = float(pindex + 1u);
                }
                else if (coord.x == int(pindex + 1u))
                {
                    fragColor.x = float(card);
                }
            }
        }

        // Calculate the player (or dealers) new hand
        uint cardValue = 0u;
        uint cardType = 0u;
        unpackCard(card, cardValue, cardType);

        if (cardValue <= 1u) // Ace
        {
            if (dealPlayerNewCard)
            {
                scores.y += 1u;
            }
            else
            {
                scores.w += 1u;
            }
        }
        else
        {
            uint addon = (cardValue < 11u) ? cardValue : 10u;
            if (dealPlayerNewCard)
            {
                scores.x += addon;
            }
            else
            {
                scores.z += addon;
            }
        }
    }

    // Handle user input
    if ((buttonStates.y == BUTTON_STATE_CLIK) && (playerState != GAME_STATE_STANDING))
    {
        gameState = GAME_STATE_DEAL_P_CARD;
        eventTimer = EVENT_TIMER_DEAL_AMOUNT;
    }
    else if (buttonStates.z == BUTTON_STATE_CLIK)
    {
        playerState = GAME_STATE_STANDING;
        gameState = GAME_STATE_DEAL_P_CARD;
        eventTimer = EVENT_TIMER_DEAL_AMOUNT;
    }

    uint playerScore = calcHandValue(scores.x, scores.y);
    uint dealerScore = calcHandValue(scores.z, scores.w);

    if ((playerScore == 21u) || (dealerScore > 21u))
    {
        playerState = GAME_STATE_WON;
        dealerState = GAME_STATE_LOST;
        gameState = GAME_STATE_END;
    }
    else if ((dealerScore == 21u) || (playerScore > 21u))
    {
        playerState = GAME_STATE_LOST;
        dealerState = GAME_STATE_WON;
        gameState = GAME_STATE_END;
    }
    else if (gameState != GAME_STATE_END)
    {
        if (dealerScore >= 17u)
        {
            dealerState = GAME_STATE_STANDING;
        }
        else if ((playerState == GAME_STATE_STANDING) && (dealerScore > playerScore))
        {
            dealerState = GAME_STATE_STANDING;
        }
    }

    if ((playerState == GAME_STATE_STANDING) && (dealerState == GAME_STATE_STANDING))
    {
        if (dealerScore < playerScore)
        {
            playerState = GAME_STATE_WON;
            dealerState = GAME_STATE_LOST;
        }
        else if (dealerScore > playerScore)
        {
            playerState = GAME_STATE_LOST;
            dealerState = GAME_STATE_WON;
        }
        else
        {
            playerState = GAME_STATE_DRAW;
            dealerState = GAME_STATE_DRAW;
        }

        gameState = GAME_STATE_END;
    }

    // Visually disable buttons for this frame
    if ((eventTimer > 0.0) || (gameState == GAME_STATE_END) || (playerState == GAME_STATE_STANDING))
    {
        buttonStates.y = BUTTON_STATE_DISABLED;
        buttonStates.z = BUTTON_STATE_DISABLED;
    }

    // Update the global state
    if (coord.y == GAME_STATE_GLOBAL_STATE_Y)
    {
        if (coord.x == GLOBAL_STATE_GAME_X)
        {
            fragColor = vec4(float(gameState), float(playerState), float(dealerState), eventTimer);
        }
        else if (coord.x == GLOBAL_STATE_TIME_X)
        {
            fragColor = vec4(prevTime, curTime, iTimeDelta, 0.0);
        }
        else if (coord.x == GLOBAL_STATE_BUTTONS_X)
        {
            fragColor = vec4(buttonStates);
        }
        else if (coord.x == GLOBAL_STATE_TOTAL_SCORE_X)
        {
            if ((ogGameState != gameState) && (gameState == GAME_STATE_END))
            {
                if (playerState == GAME_STATE_WON)
                {
                    fragColor.x += 1.0;
                }
                else if(dealerState == GAME_STATE_WON)
                {
                    fragColor.y += 1.0;
                }
            }
        }
        else if (coord.x == GLOBAL_STATE_SCORE_X)
        {
            fragColor = vec4(scores);
        }

        return;
    }
}
