const float PI = 3.141592653589793238462643383279502884197169399375105820974944592307816406286208998628034825342117067982148086513282306647093844609550582231725359408128481;
#define n1Q(p) p *= 2.*ri(round(atan(p.x, p.y) * 4.) / 4.)
#define ri(a) mat2(cos(a + asin(vec4(0,1,-1,0))))
vec4 exp_sin_lerp(vec4 a, vec4 b, float t, float s) {


    float t2 = iTime * .1 + ((.25 + .05 * sin(iTime * .1))/(length(a.xy) + .07)) * 2.2;
	float si = sin(t2);
	float co = cos(t2);
	mat2 ma = mat2(co, si, -si, co);

a.xy*=ma;
b.xy*=ma;
    float v1 = 1.0+cos(iTime) + (0.5 - 0.0) * pow(2.0 * t, s);
    float v2 = 0.5 + (1.0 - 0.5)*sin(iTime) * (1.0 - pow(2.0 - 2.0 * t, s));

a.xy*=ma;
b.xy*=ma;

    return a + (b - a) * ( t < 0.5 ?  v1 : v2);
}

vec4 get_value_2d(vec2 p) {


   p.x -= 25.15;
    float xmin = floor(p.x);
    float xmax = xmin+1.0;
    float ymin = floor(p.y);
    float ymax = ymin+1.0;

    vec4 cc1 = texelFetch(iChannel0, ivec2(xmin, ymin), 0);
    vec4 cc2 = texelFetch(iChannel0, ivec2(xmax, ymin), 0);
    vec4 cc3 = texelFetch(iChannel0, ivec2(xmin, ymax), 0);
    vec4 cc4 = texelFetch(iChannel0, ivec2(xmax, ymax), 0);

    float s = 1000.0;

    vec4 lx1 = exp_sin_lerp(cc1, cc2, p.x-xmin, s);
    vec4 lx2 = exp_sin_lerp(cc3, cc4, p.x-xmin, s);

    vec4 d = exp_sin_lerp(lx1, lx2, p.y-ymin, s);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-vec2(0.5));
      uv.x -= 10.15;
    uv *= 0.15;

    float l = 1.0;

    vec4 value = get_value_2d(uv);
    fragColor = vec4(value);
}
