#define R3D(a,A) mix(A*dot(p,A), p, cos(a)) + sin(a)*cross(p,A)
#define R2D(a) mat2( cos( a + vec4(0,11,33,0 )))

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.2,e,h; vec3 p,q=vec3(.86,.5,.57),r=iResolution;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)*r.y,e)/2.;
    //p.z-=8.*cos(iTime);
    p.xz*=R2D(iTime);

    //for(j=0.;j++<2.;)
        p=abs(abs(p=R3D(iTime*.2,vec3(1))))-.01,
        d+=max(atan(p.x,p.z),q.y),
        d-=dot(p,q)/4e4;
    e+=d;

}o+=vec4(log(fwidth(p*p)),1)*3./i;
}
