float circle(vec2 p, float r) 
{
    return length(p)-r;
}

float donut(vec2 p, vec2 t)
{
    float q = circle(p, t.x);
    return abs(q)-t.y;
}

float line(float l, float lt)
{
    return 1.-smoothstep(0., lt, abs(l));
}

float rotated_line(vec2 uv, float a, float lt)
{
    float r = radians(a);
    float rot = uv.x*cos(r)-uv.y*sin(r);
    return 1.-smoothstep(0., lt, abs(rot));
}

float rotate(float v, float a)
{
    float r = radians(a);
    return v * cos(r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    vec3 w = vec3(251,252,248)/255.;
    vec3 b = vec3(24)/255.;
    vec3 y = vec3(255, 210, 15)/255.;

    vec2 center = uv-vec2(0);
    float lt = 0.002;

    float d = donut(center, vec2(0.625, 0.125));
    vec3 color = d>0.?w:y;

    float c1 = line(circle(center, 0.5), lt);
    float c2 = line(circle(center, 0.625), lt);
    float c3 = line(circle(center, 0.75), lt);

    float l1 = abs(uv.y)>0.5&&abs(uv.y)<0.75?rotated_line(uv, 0., lt):0.;
    float l2 = uv.x>0.5&&uv.x<0.75?rotated_line(uv, 90., lt):0.;
    float l3 = uv.y>rotate(0.5, -60.)&&uv.y<rotate(0.75, -60.)?rotated_line(uv, -60., lt):0.;
    float l4 = uv.y<rotate(-0.5, 60.)&&uv.y>rotate(-0.75, 60.)?rotated_line(uv, 60., lt):0.;

    float a = atan(-uv.x,-uv.y)*0.55;
    float s = sin(a)*sin(iTime*0.5);
    float f = smoothstep(-1.55, 1.55, s)*0.25+0.51;
    float curve = line(circle(uv-vec2(0, 0.1), f), 0.008);

    color = mix(color, b, c1+c2+c3+l1+l2+l3+l4+curve);
    fragColor = vec4(color,1.0);
}
