// Tweet Ver: https://twitter.com/AlexDev21/status/1765812509594788297

#define M *=mat2(cos(iTime+vec4(0,11,33,0))) ,
#define D(X,Y) (length(max(X/Y, min(X.yzx/Y,1.))-.9)-.12)*Y
void mainImage( out vec4 O, vec2 F )
{
    O *= 0.;
    for(vec3 R = iResolution, a, q;
                 R.z++<5e1; a = vec3(0, 0, -3) + vec3((F*2.-R.xy)/R.y, 1) * O.x
                 , a.zx M  a.yx M q = a, q.xz M
                 a = abs(a), q=abs(q)
       )     O += min(min(D(q,1.), length(a)-.3), D(a,.6));


    O = fwidth(7e1/O);
}
