void mainImage(out vec4 O, in vec2 I)
{
    vec2 uv = (2. * I - iResolution.xy) / iResolution.y;
    vec2 uvMouse = (2. * iMouse.xy - iResolution.xy) / iResolution.y;
    float theta = iTime;
    for(uint i; i < 512u; i++)
    {
        uv.y += uvMouse.y;
        theta *= uvMouse.x;
        mat2 rot = mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
        uv = abs(fract(uv) - 0.5);
        uv *= rot;
    }
    float val = 0.1 / length(uv);
    vec3 col = vec3(val);
    O = vec4(col,1.0);
}
